mapper.update:
update app_version


        is_latest_version = #{isLatestVersion,jdbcType=TINYINT},


        gmt_modify = #{gmtModify,jdbcType=TIMESTAMP},


where terminal_type = #{terminalType,jdbcType=TINYINT}
AND is_latest_version = 1

    AND status = #{status,jdbcType=TINYINT}

mapper.select:
select

,

from app_version
where id = #{id,jdbcType=BIGINT}

    AND ststus = #{status}
