mapper.select:
SELECT a.id AS orderId, c.id_card AS idCard, d.repay_card_id AS repayCard FROM loan_order a LEFT JOIN bank_card_record d ON a.id= d.order_id LEFT JOIN loan_process b ON a.id = b.order_id LEFT JOIN loan_customer c ON a.loan_customer_id = c.id WHERE 1=1 AND b.bank_lend_record = 1
AND c.id_card =#{idCard}
AND d.repay_card_id =#{repayCard}

select

from
    bank_file_list_record
where
    bank_file_list_id=#{listId}

        AND name LIKE CONCAT('%',#{userName},'%')


        AND credential_no=#{idCard}


        AND is_customer=#{isCustomer}
