mapper.select:
SELECT a.id AS orderId, c.id_card AS idCard, d.repay_card_id AS repayCard FROM loan_order a LEFT JOIN bank_card_record d ON a.id= d.order_id LEFT JOIN loan_customer c ON a.loan_customer_id = c.id WHERE 1=1
AND c.id_card =#{idCard}
AND d.repay_card_id =#{repayCard}

select

from
    bank_file_list_record
where
    bank_file_list_id=#{listId}

        AND name LIKE CONCAT('%',#{userName},'%')


        AND credential_no=#{idCard}


        AND is_customer=#{isCustomer}

SELECT loan.id AS orderId, partner.`name` AS partnerName, customer.`name`, customer.id_card AS idCard, customer.mobile AS mobile, base.bank, employee.`name` AS employeeName, employee.id AS employeeId, financial.car_price AS carPrice, info.insurance_year AS insuranceYear, insurance.insurance_type AS insuranceType, insurance.start_date AS startDate, insurance.end_date AS endDate, datediff(insurance.end_date,insurance.start_date) AS days, (select start_date from insurance_relevance where insurance_info_id=info.id and insurance_type=2) AS businessStartDate, (select end_date from insurance_relevance where insurance_info_id=info.id and insurance_type=2) AS businessEndDate, case when (select renew_insurance_status from renew_insurance where loan.id=order_id order by remind_date desc LIMIT 1) is null then 0 else (select renew_insurance_status from renew_insurance where loan.id=order_id order by remind_date desc LIMIT 1) end AS renewInsuranceStatus
FROM
(select * from loan_order where 1=1
and id not in(select order_id from insurance_distribute_record)
and id in(select order_id from insurance_distribute_record)
) loan
(select * from loan_order where id in(select order_id from insurance_distribute_record where 1=1
and employee_id=#{employeeId}
and status=#{taskStatus}
) ) loan
LEFT JOIN insurance_info info on loan.id = info.order_id LEFT JOIN insurance_relevance insurance ON (info.id = insurance.insurance_info_id and insurance.insurance_type=1 ) LEFT JOIN loan_customer customer ON loan.loan_customer_id=customer.id LEFT JOIN loan_base_info base ON loan.loan_base_info_id=base.id LEFT JOIN employee ON base.salesman_id=employee.id LEFT JOIN partner ON base.partner_id=partner.id LEFT JOIN loan_financial_plan financial ON loan.loan_financial_plan_id=financial.id where datediff(insurance.end_date,now())<=31
and customer.name like concat(concat('%',#{customerName}),'%')
and base.bank = #{bankName}
and partner.id =#{partnerName}
