mapper.select:
select 

from bank_repay_record
where order_id = #{orderId,jdbcType=BIGINT}
  and bank_repay_imp_record_id = #{bankRepayImpRecordId,jdbcType=BIGINT}
  and id_card = #{idCard,jdbcType=VARCHAR}
  and repay_card = #{repayCard,jdbcType=VARCHAR}

mapper.delete:
delete from bank_repay_record
where order_id = #{orderId,jdbcType=BIGINT}
  and bank_repay_imp_record_id = #{bankRepayImpRecordId,jdbcType=BIGINT}
  and id_card = #{idCard,jdbcType=VARCHAR}
  and repay_card = #{repayCard,jdbcType=VARCHAR}

mapper.update:
update bank_repay_record
set user_name = #{userName,jdbcType=VARCHAR},
  card_balance = #{cardBalance,jdbcType=DECIMAL},
  overdue_amount = #{overdueAmount,jdbcType=DECIMAL},
  overdue_times = #{overdueTimes,jdbcType=INTEGER},
  max_overdue_times = #{maxOverdueTimes,jdbcType=INTEGER},
  is_customer = #{isCustomer,jdbcType=TINYINT},
  batch_date = #{batchDate,jdbcType=TIMESTAMP},
  status = #{status,jdbcType=TINYINT},
  gmt_create = #{gmtCreate,jdbcType=TIMESTAMP},
  gmt_modify = #{gmtModify,jdbcType=TIMESTAMP}
where order_id = #{orderId,jdbcType=BIGINT}
  and bank_repay_imp_record_id = #{bankRepayImpRecordId,jdbcType=BIGINT}
  and id_card = #{idCard,jdbcType=VARCHAR}
  and repay_card = #{repayCard,jdbcType=VARCHAR}
