mapper.select:
select 

from biz_area_rela_partner
where biz_area_id = #{bizAreaId,jdbcType=BIGINT}
  and partner_id = #{partnerId,jdbcType=BIGINT}

mapper.delete:
delete from biz_area_rela_partner
where biz_area_id = #{bizAreaId,jdbcType=BIGINT}
  and partner_id = #{partnerId,jdbcType=BIGINT}

mapper.update:
update biz_area_rela_partner


    gmt_create = #{gmtCreate,jdbcType=TIMESTAMP},


    gmt_modify = #{gmtModify,jdbcType=TIMESTAMP},


where biz_area_id = #{bizAreaId,jdbcType=BIGINT}
  and partner_id = #{partnerId,jdbcType=BIGINT}

update biz_area_rela_partner
set gmt_create = #{gmtCreate,jdbcType=TIMESTAMP},
  gmt_modify = #{gmtModify,jdbcType=TIMESTAMP}
where biz_area_id = #{bizAreaId,jdbcType=BIGINT}
  and partner_id = #{partnerId,jdbcType=BIGINT}
