mapper.select:
select

from biz_model_rela_area_partners


        AND biz_id = #{bizId,jdbcType=BIGINT}


        AND area_id = #{areaId,jdbcType=BIGINT}


        AND group_id = #{groupId,jdbcType=BIGINT}

select

from biz_model_rela_area_partners
where biz_id = #{bizId,jdbcType=BIGINT}
and area_id = #{areaId,jdbcType=BIGINT}
and group_id = #{groupId,jdbcType=BIGINT}

mapper.delete:
delete from biz_model_rela_area_partners
where biz_id = #{bizId,jdbcType=BIGINT}
  and area_id = #{areaId,jdbcType=BIGINT}
  and group_id = #{groupId,jdbcType=BIGINT}

mapper.update:
update biz_model_rela_area_partners
set gmt_modify = now()
where biz_id = #{bizId,jdbcType=BIGINT}
  and area_id = #{areaId,jdbcType=BIGINT}
  and group_id = #{groupId,jdbcType=BIGINT}
