FROM biz_model_rela_area_partners AS a
JOIN biz_model AS b
ON a.`biz_id` = b.`biz_id`

    AND a.group_id = #{id}


    AND a.area_id IN

        #{item}



    AND b.status = #{status}

select

from biz_model_rela_area_partners
where biz_id = #{bizId,jdbcType=BIGINT}
and area_id = #{areaId,jdbcType=BIGINT}
and group_id = #{groupId,jdbcType=BIGINT}

mapper.delete:
delete from biz_model_rela_area_partners
where biz_id = #{0, jdbcType=BIGINT}
  and area_id = #{1, jdbcType=BIGINT}
  and group_id = #{2, jdbcType=BIGINT}

mapper.update:
