mapper.select:
SELECT principal_area.area_name, -- 客户类型: 1-主贷人;2-共贷人;3-担保人;4-紧急联系人; CASE WHEN customer.cust_type = 1 THEN '主贷人' WHEN customer.cust_type = 2 THEN '共贷人' WHEN customer.cust_type = 3 THEN '担保人' WHEN customer.cust_type = 4 THEN '紧急联系人' ELSE NULL END AS cust_type, customer. NAME AS customer_name, customer.id_card AS customer_id_card, customer.mobile AS customer_mobile, principal_base.bank, principal_partner. NAME AS partner_name, employee. NAME AS salesman_name, principal. NAME AS principal_name, -- 与主贷人关系：0-本人;1-配偶;2-父母;3-子女;4-兄弟姐妹;5-亲戚;6-朋友;7-同学;8-同事;9-其它; CASE WHEN customer.id = customer.principal_cust_id THEN '本人' WHEN customer.cust_relation = 0 THEN '本人' WHEN customer.cust_relation = 1 THEN '配偶' WHEN customer.cust_relation = 2 THEN '父母' WHEN customer.cust_relation = 3 THEN '子女' WHEN customer.cust_relation = 4 THEN '兄弟姐妹' WHEN customer.cust_relation = 5 THEN '亲戚' WHEN customer.cust_relation = 6 THEN '朋友' WHEN customer.cust_relation = 7 THEN '同学' WHEN customer.cust_relation = 8 THEN '同事' WHEN customer.cust_relation = 9 THEN '其他' ELSE NULL END AS cust_relation, -- 征信结果: 0-不通过;1-通过;2-关注; CASE WHEN credit_info.result = 0 THEN '不通过' WHEN credit_info.result = 1 THEN '通过' WHEN credit_info.result = 2 THEN '关注' WHEN credit_info.result = 3 THEN '银行退回' ELSE NULL END AS credit_result, CASE WHEN customer.guarantee_type = 1 THEN '银行担保' WHEN customer.guarantee_type = 2 THEN '内部担保' ELSE NULL END AS guaranteeType, ( SELECT create_time FROM loan_process_log WHERE task_definition_key = 'usertask_credit_apply' AND order_id = principal_orders.id ORDER BY create_time LIMIT 1 ) AS credit_apply_time, credit_info.gmt_modify AS credit_query_time, ( SELECT user_name FROM loan_process_log WHERE task_definition_key = 'usertask_bank_credit_record' AND order_id = principal_orders.id ORDER BY create_time LIMIT 1 ) AS gmt_user FROM loan_customer customer LEFT JOIN loan_credit_info credit_info ON credit_info.customer_id = customer.id LEFT JOIN loan_customer principal ON customer.principal_cust_id = principal.id LEFT JOIN loan_order principal_orders ON principal_orders.loan_customer_id = principal.id LEFT JOIN loan_base_info principal_base ON principal_orders.loan_base_info_id = principal_base.id LEFT JOIN partner principal_partner ON principal_partner.id = principal_base.partner_id LEFT JOIN base_area principal_area ON principal_area.area_id = principal_base.area_id LEFT JOIN employee employee ON employee.id = principal_base.salesman_id LEFT JOIN loan_process ON principal_orders.id = loan_process.order_id
and (credit_info.type = 2 OR credit_info.type is NULL) AND loan_process.social_credit_record IN (1,2)
and credit_info.gmt_modify between #{startDate} and #{endDate}
and principal_base.partner_id in
#{item}
and principal_base.bank in
#{item}
AND customer.cust_type in (1,2,3) and customer.status = 0
having( TO_DAYS(credit_apply_time) >= TO_DAYS(#{startCreditGmtCreate}) AND TO_DAYS(credit_apply_time) <= TO_DAYS(#{endCreditGmtCreate}) )
order by credit_info.gmt_modify desc
