select tab.* from ( SELECT (SELECT biz_area.name FROM biz_area_rela_partner LEFT JOIN biz_area ON biz_area_rela_partner.biz_area_id = biz_area.id WHERE biz_area_rela_partner.partner_id = partner.id ORDER BY biz_area_rela_partner.gmt_create limit 1 ) AS biz_area, (SELECT biz_area.id FROM biz_area_rela_partner LEFT JOIN biz_area ON biz_area_rela_partner.biz_area_id = biz_area.id WHERE biz_area_rela_partner.partner_id = partner.id ORDER BY biz_area_rela_partner.gmt_create limit 1 ) AS biz_area_id,
base_area.area_name, customer. NAME AS customer_name, customer.id_card AS customer_id_card, customer.mobile AS customer_mobile, loaninfo.bank, partner. NAME AS partner_name, employee. NAME AS salesman_name, case when car.car_type = 0 then '新车' when car.car_type = 1 then '二手车' else '其他' end as car_type, loan_financial_plan.car_price AS price, loan_financial_plan.sign_rate, loan_financial_plan.loan_amount, loan_financial_plan.down_payment_money, loan_financial_plan.bank_period_principal, remit_details.remit_amount, ( SELECT create_time FROM loan_process_log WHERE task_definition_key = 'usertask_credit_apply' AND order_id = orders.id ORDER BY create_time LIMIT 1 ) AS gmt_create, ( SELECT loan_process_log.create_time FROM loan_process_log WHERE order_id = orders.id AND task_definition_key = 'usertask_remit_review' ORDER BY create_time DESC LIMIT 1 ) as submitTime, ( SELECT loan_process_log.user_name FROM loan_process_log WHERE order_id = orders.id AND task_definition_key = 'usertask_remit_review' ORDER BY create_time DESC LIMIT 1 ) as username, loan_financial_plan.loan_time as loan_time FROM loan_order orders LEFT JOIN loan_customer customer ON orders.loan_customer_id = customer.id LEFT JOIN loan_base_info loaninfo ON orders.loan_base_info_id = loaninfo.id LEFT JOIN base_area ON loaninfo.area_id = base_area.area_id LEFT JOIN loan_car_info car ON orders.loan_car_info_id = car.id LEFT JOIN car_detail cardetail ON cardetail.id = car.car_detail_id LEFT JOIN loan_financial_plan ON orders.loan_financial_plan_id = loan_financial_plan.id LEFT JOIN remit_details ON orders.remit_details_id = remit_details.id LEFT JOIN loan_process process ON orders.id = process.order_id LEFT JOIN loan_base_info base ON orders.loan_base_info_id = base.id LEFT JOIN employee employee ON base.salesman_id = employee.id LEFT JOIN partner partner ON partner.id = base.partner_id where 1=1 and process.remit_review = 1
and base.partner_id in
#{item}
and base.bank in
#{item}
having
gmt_create >= #{startDate}
AND
gmt_create <= #{endDate}
order by submitTime desc ) tab
tab.biz_area_id in
#{item}
order by gmt_create desc
order by gmt_create ASC

select tab.* from ( SELECT (SELECT biz_area.name FROM biz_area_rela_partner LEFT JOIN biz_area ON biz_area_rela_partner.biz_area_id = biz_area.id WHERE biz_area_rela_partner.partner_id = partner.id ORDER BY biz_area_rela_partner.gmt_create limit 1 ) AS biz_area, (SELECT biz_area.id FROM biz_area_rela_partner LEFT JOIN biz_area ON biz_area_rela_partner.biz_area_id = biz_area.id WHERE biz_area_rela_partner.partner_id = partner.id ORDER BY biz_area_rela_partner.gmt_create limit 1 ) AS biz_area_id,
base_area.area_name, partner.NAME AS partner_name, customer.NAME AS customer_name, customer.id_card, customer.mobile AS customer_mobile, ( SELECT create_time FROM loan_process_log WHERE task_definition_key = 'usertask_credit_apply' AND order_id = orders.id ORDER BY create_time LIMIT 1 ) AS credit_apply_time, loanbaseinfo.bank, CASE WHEN car.car_type = 0 THEN '新车' WHEN car.car_type = 1 THEN '二手车' ELSE '其他' END AS car_type, vehicle_information.license_plate_number, cardetail.price, loan_financial_plan.loan_amount, loan_financial_plan.bank_period_principal, ( SELECT create_time FROM loan_process_log WHERE orders.id = loan_process_log.order_id AND loan_process_log.task_definition_key = 'usertask_remit_review' ORDER BY id DESC LIMIT 1 ) AS remitdate, bank_lend_record.lend_date, ( SELECT loan_data_flow.express_send_date FROM loan_data_flow WHERE orders.id = loan_data_flow.order_id AND type = 12 ORDER BY id DESC LIMIT 1 ) AS sendMaterialToParter_date, ( SELECT loan_data_flow.express_receive_date FROM loan_data_flow WHERE orders.id = loan_data_flow.order_id AND type = 12 ORDER BY id DESC LIMIT 1 ) AS ParterReceiveMaterial_date, CASE WHEN process.apply_license_plate_deposit_info = 1 THEN '已提交' WHEN process.apply_license_plate_deposit_info = 2 THEN '未提交' WHEN process.apply_license_plate_deposit_info = 3 THEN '已打回' ELSE '未知'
END AS apply_license_plate_deposit_info, CASE WHEN process.apply_license_plate_deposit_info !=1 THEN NULL ELSE a.apply_license_plate_deposit_date END AS depositTime,
CASE WHEN a.apply_license_plate_deposit_date IS NULL THEN floor( ( UNIX_TIMESTAMP( DATE_FORMAT( now( ), '%Y-%m-%d' )) - UNIX_TIMESTAMP( DATE_FORMAT( lend.lend_date, '%Y-%m-%d' ) ) ) / ( 24 * 60 * 60 ) ) ELSE floor ( (UNIX_TIMESTAMP( DATE_FORMAT(a.apply_license_plate_deposit_date, '%Y-%m-%d' )) - UNIX_TIMESTAMP( DATE_FORMAT( lend.lend_date, '%Y-%m-%d' ) ) ) / ( 24 * 60 * 60 ) ) END AS depositOverdueTime, ( SELECT user_name FROM loan_process_log WHERE order_id = orders.id AND task_definition_key = 'usertask_apply_license_plate_deposit_info' ORDER BY create_time DESC LIMIT 1 ) AS submitUser
FROM loan_order orders LEFT JOIN loan_customer customer ON orders.loan_customer_id = customer.id LEFT JOIN bank_lend_record lend ON lend.id = orders.bank_lend_record_id LEFT JOIN loan_base_info loanbaseinfo ON orders.loan_base_info_id = loanbaseinfo.id LEFT JOIN base_area ON loanbaseinfo.area_id = base_area.area_id LEFT JOIN loan_car_info car ON orders.loan_car_info_id = car.id LEFT JOIN car_detail cardetail ON cardetail.id = car.car_detail_id LEFT JOIN vehicle_information ON vehicle_information.id = orders.vehicle_information_id LEFT JOIN loan_financial_plan ON orders.loan_financial_plan_id = loan_financial_plan.id LEFT JOIN remit_details ON orders.remit_details_id = remit_details.id LEFT JOIN bank_lend_record ON orders.bank_lend_record_id = bank_lend_record.id LEFT JOIN loan_process process ON orders.id = process.order_id LEFT JOIN loan_base_info base ON orders.loan_base_info_id = base.id LEFT JOIN employee employee ON base.salesman_id = employee.id LEFT JOIN partner partner ON partner.id = base.partner_id LEFT JOIN apply_license_plate_deposit_info a ON a.id = orders.apply_license_plate_deposit_info_id
and process.apply_license_plate_deposit_info != 0
and base.partner_id in
#{item}
and base.bank in
#{item}
having
credit_apply_time >= #{startDate}
AND
credit_apply_time <= #{endDate}
) tab
tab.biz_area_id in
#{item}
order by tab.credit_apply_time desc
order by tab.credit_apply_time ASC

SELECT orders.id AS orderId, customer.NAME AS customer_name, customer.id_card AS customer_id_card, customer.mobile AS customer_mobile, loaninfo.bank, partner. NAME AS partner_name, employee. NAME AS salesman_name, case when car.car_type = 0 then '新车' when car.car_type = 1 then '二手车' else '其他' end as car_type, loan_financial_plan.car_price AS price, loan_financial_plan.sign_rate, loan_financial_plan.loan_amount, loan_financial_plan.down_payment_money, loan_financial_plan.bank_period_principal, remit_details.remit_amount, CASE WHEN parea.parent_area_id = '' OR parea.parent_area_id = NULL THEN CONCAT(area.parent_area_name,area.area_name) ELSE CONCAT(parea.parent_area_name,area.area_name) END AS apply_license_plate_area, ( SELECT loan_process_log.create_time FROM loan_process_log WHERE order_id = orders.id AND task_definition_key = 'usertask_remit_review' ORDER BY create_time DESC LIMIT 1 ) as submitTime, ( SELECT loan_process_log.user_name FROM loan_process_log WHERE order_id = orders.id AND task_definition_key = 'usertask_remit_review' ORDER BY create_time DESC LIMIT 1 ) as username FROM loan_order orders LEFT JOIN loan_customer customer ON orders.loan_customer_id = customer.id LEFT JOIN loan_base_info loaninfo ON orders.loan_base_info_id = loaninfo.id LEFT JOIN base_area ON loaninfo.area_id = base_area.area_id LEFT JOIN loan_car_info car ON orders.loan_car_info_id = car.id LEFT JOIN car_detail cardetail ON cardetail.id = car.car_detail_id LEFT JOIN loan_financial_plan ON orders.loan_financial_plan_id = loan_financial_plan.id LEFT JOIN remit_details ON orders.remit_details_id = remit_details.id LEFT JOIN loan_process process ON orders.id = process.order_id LEFT JOIN loan_base_info base ON orders.loan_base_info_id = base.id LEFT JOIN employee employee ON base.salesman_id = employee.id LEFT JOIN partner partner ON partner.id = base.partner_id
LEFT JOIN vehicle_information ON orders.vehicle_information_id = vehicle_information.id LEFT JOIN base_area area on vehicle_information.apply_license_plate_area = area.area_id LEFT JOIN base_area parea ON area.parent_area_id = parea.area_id where 1=1 and process.remit_review = 1
having submitTime between #{startDate} and #{endDate}
