mapper.select:
select 

from conf_loan_apply
where bank = #{bank,jdbcType=VARCHAR}
  and car_type = #{car_type,jdbcType=INTEGER}

mapper.delete:
delete from conf_loan_apply
where bank = #{bank,jdbcType=VARCHAR}
  and car_type = #{car_type,jdbcType=INTEGER}

mapper.insert:
insert into conf_loan_apply (bank, car_type, down_payment_ratio, 
  loan_ratio, financial_service_fee, staging_ratio, 
  down_payment_ratio_compare, loan_ratio_compare, 
  staging_ratio_compard, financial_service_fee_compard
  )
values (#{bank,jdbcType=VARCHAR}, #{car_type,jdbcType=INTEGER}, #{down_payment_ratio,jdbcType=DECIMAL}, 
  #{loan_ratio,jdbcType=DECIMAL}, #{financial_service_fee,jdbcType=DECIMAL}, #{staging_ratio,jdbcType=DECIMAL}, 
  #{down_payment_ratio_compare,jdbcType=VARCHAR}, #{loan_ratio_compare,jdbcType=VARCHAR}, 
  #{staging_ratio_compard,jdbcType=VARCHAR}, #{financial_service_fee_compard,jdbcType=VARCHAR}
  )
