mapper.select:
select COUNT(*)
from department_rela_user_group
WHERE  department_id = #{id}

select

from department_rela_user_group
WHERE department_id = #{id}
ORDER BY gmt_modify DESC
limit #{startRow}, #{pageSize}

select  user_group_id
from department_rela_user_group
where department_id = #{departmentId,jdbcType=BIGINT}

select  department_id
from department_rela_user_group
where user_group_id = #{userGroupId,jdbcType=BIGINT}

select

from department_rela_user_group
where department_id = #{departmentId,jdbcType=BIGINT}
and user_group_id = #{userGroupId,jdbcType=BIGINT}

mapper.delete:
delete from department_rela_user_group
where department_id = #{departmentId,jdbcType=BIGINT}
  and user_group_id = #{userGroupId,jdbcType=BIGINT}

mapper.update:
update department_rela_user_group


        gmt_create = #{gmtCreate,jdbcType=TIMESTAMP},


        gmt_modify = #{gmtModify,jdbcType=TIMESTAMP},


where department_id = #{departmentId,jdbcType=BIGINT}
and user_group_id = #{userGroupId,jdbcType=BIGINT}

update department_rela_user_group
set gmt_create = #{gmtCreate,jdbcType=TIMESTAMP},
  gmt_modify = #{gmtModify,jdbcType=TIMESTAMP}
where department_id = #{departmentId,jdbcType=BIGINT}
  and user_group_id = #{userGroupId,jdbcType=BIGINT}
