mapper.select:
select 

from employee_rela_biz_area
where employee_id = #{employeeId,jdbcType=BIGINT}
  and biz_area_id = #{bizAreaId,jdbcType=BIGINT}

mapper.delete:
delete from employee_rela_biz_area
where employee_id = #{employeeId,jdbcType=BIGINT}
  and biz_area_id = #{bizAreaId,jdbcType=BIGINT}

mapper.update:
update employee_rela_biz_area


    gmt_create = #{gmtCreate,jdbcType=TIMESTAMP},


    gmt_modify = #{gmtModify,jdbcType=TIMESTAMP},


where employee_id = #{employeeId,jdbcType=BIGINT}
  and biz_area_id = #{bizAreaId,jdbcType=BIGINT}
