mapper.select:
SELECT foe.order_id AS orderId, DATE_FORMAT(foe.operation_exception_time, '%Y-%m-%d %H:%i:%S' ) AS operationExceptionTime, loan_customer.`name` AS customerName, loan_customer.id_card AS idCard, loan_base_info.bank AS bank, loan_car_info.car_type AS carType, partner.`name` AS partnerName, foe.`status` AS operationStatus, foe.operation_exception_reason AS operationExceptionReason
FROM financial_operation_exception foe LEFT JOIN loan_order ON foe.order_id = loan_order.id LEFT JOIN loan_customer ON loan_order.loan_customer_id = loan_customer.id LEFT JOIN loan_base_info ON loan_order.loan_base_info_id = loan_base_info.id LEFT JOIN loan_car_info ON loan_order.loan_car_info_id = loan_car_info.id LEFT JOIN partner ON loan_base_info.partner_id = partner.id
AND loan_customer.`name` LIKE CONCAT('%',#{customerName},'%')
AND loan_customer.id_card = #{idCard}
AND loan_base_info.bank = #{loanBank}
AND foe.order_id = #{orderId}
AND DATE_FORMAT(foe.operation_exception_time, '%Y-%m-%d' ) = #{operationExceptionTime}
AND foe.`status` = #{status}
AND loan_car_info.car_type = #{carType}
