mapper.select:
select

from financial_product
where

  bank_name = #{bankName,jdbcType=VARCHAR}
  AND


  biz_type = #{bizType,jdbcType=TINYINT}
  AND


  category_superior = #{category_superior,jdbcType=VARCHAR}
  AND


  category_junior = #{category_junior,jdbcType=VARCHAR}
  AND


  rate = #{rate,jdbcType=VARCHAR}
  AND


  area_id = #{area_id,jdbcType=BIGINT}
