mapper.select:
select
from inst_loan_order
LEFT JOIN act_ru_task ON inst_loan_order.process_inst_id = act_ru_task.PROC_INST_ID_
where 1=1
AND order_nbr = #{orderNbr,jdbcType=VARCHAR}
AND cust_id IN (SELECT cust_id FROM cust_base_info WHERE cust_name LIKE CONCAT('%',#{custName},'%'))
AND cust_id IN (SELECT cust_id FROM cust_base_info WHERE phone = #{phone,jdbcType=VARCHAR})
AND cust_id IN (SELECT cust_id FROM cust_base_info WHERE identity_number = #{identityNumber,jdbcType=VARCHAR})
AND area_id = #{areaId,jdbcType=BIGINT}
AND partner_id = #{partnerId,jdbcType=BIGINT}
AND process_inst_id IN (SELECT PROC_INST_ID_ FROM act_ru_task WHERE TASK_DEF_KEY_ = #{todoProcessTask,jdbcType=VARCHAR})
AND process_inst_id IN (SELECT PROC_INST_ID_ FROM act_hi_taskinst WHERE TASK_DEF_KEY_ = #{doneProcessTask,jdbcType=VARCHAR} AND act_hi_taskinst.END_TIME_ <= now())
and gmt_create >= #{startDate}
and gmt_create <= #{endDate}
AND status = #{status}
