mapper.select:
select 

from loan_apply_compensation
where order_id = #{orderId,jdbcType=BIGINT}
  and apply_compensation_date = #{applyCompensationDate,jdbcType=TIMESTAMP}

mapper.delete:
delete from loan_apply_compensation
where order_id = #{orderId,jdbcType=BIGINT}
  and apply_compensation_date = #{applyCompensationDate,jdbcType=TIMESTAMP}
