mapper.select:
select

from loan_credit_info
where customer_id = #{customerId,jdbcType=BIGINT}

    AND type = #{type}

mapper.insert:

    SELECT LAST_INSERT_ID()

insert into loan_credit_info (id, customer_id, result,
info, type, gmt_create,
gmt_modify, status, feature
)
values (#{id,jdbcType=BIGINT}, #{customerId,jdbcType=BIGINT}, #{result,jdbcType=TINYINT},
#{info,jdbcType=VARCHAR}, #{type,jdbcType=TINYINT}, #{gmtCreate,jdbcType=TIMESTAMP},
#{gmtModify,jdbcType=TIMESTAMP}, #{status,jdbcType=TINYINT}, #{feature,jdbcType=VARCHAR}
)
