mapper.select:
select
f.id AS id, f.customer_id AS customerId, f.info_supplement_id AS infoSupplementId, f.path AS path, f.type AS type, f.upload_type AS uploadType, f.gmt_create AS gmtCreate, f.gmt_modify AS gmtModify, f.status AS status
FROM loan_file f, loan_info_supplement s
WHERE
/*资料流转已提交*/ s.id = f.info_supplement_id AND s.status = 1
AND customer_id = #{customerId,jdbcType=BIGINT}
AND type = #{type}
AND upload_type = #{uploadType}

mapper.insert:
INSERT INTO loan_file
(customer_id, info_supplement_id,
path, type, upload_type,
gmt_create, gmt_modify, status
)
VALUES

    (#{item.customerId,jdbcType=BIGINT}, #{item.infoSupplementId,jdbcType=BIGINT},
    #{item.path,jdbcType=VARCHAR}, #{item.type,jdbcType=TINYINT}, #{item.uploadType,jdbcType=TINYINT},
    #{item.gmtCreate,jdbcType=TIMESTAMP}, #{item.gmtModify,jdbcType=TIMESTAMP}, #{item.status,jdbcType=TINYINT}
    )

insert into loan_file (id, customer_id, info_supplement_id,
path, type, upload_type,
gmt_create, gmt_modify, status
)
values (#{id,jdbcType=BIGINT}, #{customerId,jdbcType=BIGINT}, #{infoSupplementId,jdbcType=BIGINT},
#{path,jdbcType=VARCHAR}, #{type,jdbcType=TINYINT}, #{uploadType,jdbcType=TINYINT},
#{gmtCreate,jdbcType=TIMESTAMP}, #{gmtModify,jdbcType=TIMESTAMP}, #{status,jdbcType=TINYINT}
)
