mapper.select:
select

from loan_file
where customer_id = #{customerId,jdbcType=BIGINT}

    AND type = #{type}


    AND upload_type = #{uploadType}

mapper.insert:
insert into loan_file (id, customer_id, path,
type, upload_type, gmt_create,
gmt_modify, status, feature
)
values

    (#{id,jdbcType=BIGINT}, #{customerId,jdbcType=BIGINT}, #{path,jdbcType=VARCHAR},
    #{type,jdbcType=TINYINT}, #{uploadType,jdbcType=TINYINT}, #{gmtCreate,jdbcType=TIMESTAMP},
    #{gmtModify,jdbcType=TIMESTAMP}, #{status,jdbcType=TINYINT}, #{feature,jdbcType=VARCHAR}
    )


    SELECT LAST_INSERT_ID()

insert into loan_file (id, customer_id, path,
