mapper.select:
select path AS urls, type AS type, useFileTypeCastName(type) AS name from loan_file where customer_id = (select loan_customer_id from loan_order where id = #{orderId,jdbcType=BIGINT})
and type in
#{item}

select path AS urls, type AS type, useFileTypeCastName(type) AS name from loan_file where customer_id = #{customerId,jdbcType=BIGINT}
and type in
#{item}

select serial.reject_reason from bank_interface_serial serial
inner join
(
select SUBSTRING_INDEX(GROUP_CONCAT(serial_no order by request_time desc separator ','),',',1)
				as serial_no
from (select * from bank_interface_serial where status = 3) t GROUP BY trans_code,customer_id
) orderdata on orderdata.serial_no = serial.serial_no and serial.trans_code = #{transCode}
and serial.customer_id = #{customerId}

select serial.api_status,serial.api_msg,serial.status,serial.reject_reason,serial.request_time,
       serial.callback_time,serial.file_num,serial.trans_code
from bank_interface_serial serial
inner join
(
select SUBSTRING_INDEX(GROUP_CONCAT(serial_no order by request_time desc separator ','),',',1)
				as serial_no
from bank_interface_serial GROUP BY trans_code,customer_id
) orderdata on orderdata.serial_no = serial.serial_no and serial.trans_code = #{transCode}
and serial.customer_id = #{customerId}

select serial.status from bank_interface_serial serial
inner join
(
select SUBSTRING_INDEX(GROUP_CONCAT(serial_no order by request_time desc separator ','),',',1)
as serial_no
from bank_interface_serial t GROUP BY trans_code,customer_id
) orderdata on orderdata.serial_no = serial.serial_no and serial.trans_code = #{transCode}
and serial.customer_id = #{customerId}

select
    ss.order_id,
    ss.status,
    ss.api_status
 from (
        select serial.order_id,serial.status,serial.api_status  from bank_interface_serial serial
        inner join
        (
        select SUBSTRING_INDEX(GROUP_CONCAT(serial_no order by request_time desc separator ','),',',1)
        as serial_no
        from bank_interface_serial GROUP BY trans_code,customer_id
        ) orderdata on orderdata.serial_no = serial.serial_no and serial.trans_code = #{transCode}
) ss GROUP BY  ss.order_id having order_id = #{orderId}

SELECT a.id AS orderId, c.id_card AS idCard, c.name AS customerName, e.name AS salesmanName, c.mobile AS mobile, d.name AS departmentName, p.name AS partnerName, b.bank AS bankName, f.id AS id, f.flow_out_dept_id AS flowOutDeptId, f.flow_out_dept_name AS flowOutDeptName, f.flow_in_dept_id AS flowInDeptId, f.flow_in_dept_name AS flowInDeptName, f.express_com AS expressCom, f.express_num AS expressNum, f.express_send_date AS expressSendDate, f.express_receive_date AS expressReceiveDate, f.express_receive_man AS expressReceiveMan, f.has_mortgage_contract AS hasMortgageContract, f.type AS type, f.status AS status, f.info AS info, f.gmt_create AS gmtCreate, f.gmt_modify AS gmtModify
FROM loan_order a LEFT JOIN loan_data_flow f ON a.id = f.order_id LEFT JOIN loan_base_info b ON b.id = a.loan_base_info_id LEFT JOIN loan_customer c ON c.id = a.loan_customer_id LEFT JOIN employee e ON e.id = b.salesman_id LEFT JOIN partner p ON p.id = b.partner_id LEFT JOIN department d ON d.id = p.department_id
WHERE f.id = #{dataFlowId}

SELECT a.id AS orderId, c.id_card AS idCard, c.name AS customerName, c.mobile AS mobile, e.name AS salesmanName, d.name AS departmentName, p.name AS partnerName, b.bank AS bankName, m.material_num AS materialNum, m.complete_date AS completeDate, m.info AS info, m.gmt_create AS gmtCreate, m.gmt_modify AS gmtModify
FROM loan_order a LEFT JOIN loan_material_manage m ON a.id = m.order_id LEFT JOIN loan_base_info b ON b.id = a.loan_base_info_id LEFT JOIN loan_customer c ON c.id = a.loan_customer_id LEFT JOIN employee e ON e.id = b.salesman_id LEFT JOIN partner p ON p.id = b.partner_id LEFT JOIN department d ON d.id = p.department_id
WHERE a.id = #{orderId}

select
     id, serial_no, file_name, file_path, file_type, success, request_time, error
 from bank_interface_file_serial where success = 1 and serial_no = #{serialNo} and file_type = #{fileType}

SELECT a.id AS orderId, c.id_card AS idCard, c.name AS customerName, c.mobile AS mobile, r.repay_card_id AS repayCardNum, c.bank_card_transmit_address AS bankCardTransmitAddress, e.name AS salesmanName, d.name AS departmentName, p.name AS partnerName, b.bank AS bankName, s.cardholder_name AS cardholderName, s.cardholder_phone AS cardholderPhone, s.cardholder_address AS cardholderAddress, s.express_send_address AS expressSendAddress, s.express_send_num AS expressSendNum, s.express_send_date AS expressSendDate, s.gmt_create AS gmtCreate, s.gmt_modify AS gmtModify
FROM loan_order a LEFT JOIN loan_bank_card_send s ON a.id = s.order_id LEFT JOIN bank_card_record r ON a.id = r.order_id LEFT JOIN loan_base_info b ON b.id = a.loan_base_info_id LEFT JOIN loan_customer c ON c.id = a.loan_customer_id LEFT JOIN employee e ON e.id = b.salesman_id LEFT JOIN partner p ON p.id = b.partner_id LEFT JOIN department d ON d.id = p.department_id
WHERE a.id = #{orderId}

SELECT a.id AS orderId, c.id_card AS idCard, c.name AS customerName, e.name AS salesmanName, c.mobile AS mobile, d.name AS departmentName, p.name AS partnerName, b.bank AS bankName, l.car_type AS carType, useGetCarName(l.car_detail_id) AS carDetailName, l.gps_num AS gpsNum, g.license_plate_number AS licensePlateNymber, p.gps_company AS gpsCompany
FROM loan_order a LEFT JOIN loan_base_info b ON b.id = a.loan_base_info_id LEFT JOIN loan_customer c ON c.id = a.loan_customer_id LEFT JOIN employee e ON e.id = b.salesman_id LEFT JOIN partner p ON p.id = b.partner_id LEFT JOIN department d ON d.id = p.department_id LEFT JOIN loan_car_info l ON l.id = a.loan_car_info_id LEFT JOIN vehicle_information g ON g.id = a.vehicle_information_id
WHERE a.id = #{orderId}
