mapper.select:
select 

from loan_repay_plan
where order_id = #{orderId,jdbcType=BIGINT}
  and repay_date = #{repayDate,jdbcType=TIMESTAMP}

mapper.delete:
delete from loan_repay_plan
where order_id = #{orderId,jdbcType=BIGINT}
  and repay_date = #{repayDate,jdbcType=TIMESTAMP}

mapper.insert:
insert into loan_repay_plan (order_id, repay_date, payable_amount, 
  actual_repay_amount, is_overdue, overdue_amount, 
  check_date, status, nper
  )
values (#{orderId,jdbcType=BIGINT}, #{repayDate,jdbcType=TIMESTAMP}, #{payableAmount,jdbcType=DECIMAL}, 
  #{actualRepayAmount,jdbcType=DECIMAL}, #{isOverdue,jdbcType=TINYINT}, #{overdueAmount,jdbcType=DECIMAL}, 
  #{checkDate,jdbcType=TIMESTAMP}, #{status,jdbcType=TINYINT}, #{nper,jdbcType=INTEGER}
  )

mapper.update:
update loan_repay_plan
set payable_amount = #{payableAmount,jdbcType=DECIMAL},
  actual_repay_amount = #{actualRepayAmount,jdbcType=DECIMAL},
  is_overdue = #{isOverdue,jdbcType=TINYINT},
  overdue_amount = #{overdueAmount,jdbcType=DECIMAL},
  check_date = #{checkDate,jdbcType=TIMESTAMP},
  status = #{status,jdbcType=TINYINT},
  nper = #{nper,jdbcType=INTEGER}
where order_id = #{orderId,jdbcType=BIGINT}
  and repay_date = #{repayDate,jdbcType=TIMESTAMP}
