SELECT t.*, CASE
WHEN t.express_receive_date IS NOT NULL THEN floor( ( UNIX_TIMESTAMP( DATE_FORMAT( t.express_receive_date, '%Y-%m-%d' ) ) - UNIX_TIMESTAMP( DATE_FORMAT( t.remitdate, '%Y-%m-%d' ) ) ) / ( 24 * 60 * 60 ) ) ELSE floor( ( UNIX_TIMESTAMP( DATE_FORMAT( now( ), '%Y-%m-%d' ) ) - UNIX_TIMESTAMP( DATE_FORMAT( t.remitdate, '%Y-%m-%d' ) ) ) / ( 24 * 60 * 60 ) ) END AS remitOverdueTime, CASE
WHEN t.complete_material_date IS NOT NULL THEN floor( ( UNIX_TIMESTAMP( DATE_FORMAT( t.complete_material_date, '%Y-%m-%d' ) ) - UNIX_TIMESTAMP( DATE_FORMAT( t.remitdate, '%Y-%m-%d' ) ) ) / ( 24 * 60 * 60 ) ) ELSE floor( ( UNIX_TIMESTAMP( DATE_FORMAT( now( ), '%Y-%m-%d' ) ) - UNIX_TIMESTAMP( DATE_FORMAT( t.remitdate, '%Y-%m-%d' ) ) ) / ( 24 * 60 * 60 ) ) END AS revievOverdueTime FROM ( SELECT base_area.area_name, partner.NAME AS partner_name, customer.NAME AS customer_name, customer.id_card, loan_base_info.bank, loan_financial_plan.bank_period_principal, ( SELECT create_time FROM loan_process_log WHERE orders.id = loan_process_log.order_id AND loan_process_log.task_definition_key = 'usertask_remit_review' ORDER BY id DESC LIMIT 1 ) AS remitdate, ( SELECT loan_data_flow.express_receive_date FROM loan_data_flow WHERE orders.id = loan_data_flow.order_id AND type = 2 ORDER BY loan_data_flow.id DESC LIMIT 1 ) AS express_receive_date, material_audit.complete_material_date, ( SELECT create_time FROM loan_process_log WHERE orders.id = loan_process_log.order_id AND loan_process_log.task_definition_key = 'usertask_material_review' ORDER BY id DESC LIMIT 1 ) AS materialReviewSubmit, CASE
WHEN loan_process.material_review = 1 THEN '已提交' WHEN loan_process.material_review = 2 THEN '未提交' WHEN loan_process.material_review = 3 THEN '已打回' ELSE '未知' END AS material_review, ( SELECT count( 1 ) FROM loan_info_supplement WHERE loan_info_supplement.order_id = orders.id ) AS supplementCount, ( SELECT GROUP_CONCAT( DATE_FORMAT( end_time, '%Y-%m-%d ' ), content ORDER BY id DESC ) FROM loan_info_supplement WHERE loan_info_supplement.order_id = orders.id ) AS supplementContent, ( SELECT GROUP_CONCAT( DATE_FORMAT( start_time, '%Y-%m-%d至' ), DATE_FORMAT( end_time, '%Y-%m-%d' ) ORDER BY id DESC ) FROM loan_info_supplement WHERE loan_info_supplement.order_id = orders.id ) AS supplementTime, ( SELECT create_time FROM loan_process_log WHERE orders.id = loan_process_log.order_id AND task_definition_key = 'usertask_vehicle_information' ORDER BY id DESC LIMIT 1 ) AS carMaterialCreateTime, ( SELECT loan_data_flow.express_send_date FROM loan_data_flow WHERE orders.id = loan_data_flow.order_id AND （type = 3 or type = 4) ORDER BY id DESC LIMIT 1 ) AS express_sendbank_date FROM loan_order orders LEFT JOIN loan_customer customer ON orders.loan_customer_id = customer.id LEFT JOIN loan_base_info ON orders.loan_base_info_id = loan_base_info.id LEFT JOIN loan_financial_plan ON orders.loan_financial_plan_id = loan_financial_plan.id LEFT JOIN loan_base_info base ON orders.loan_base_info_id = base.id LEFT JOIN partner partner ON partner.id = base.partner_id LEFT JOIN remit_details ON orders.remit_details_id = remit_details.id LEFT JOIN base_area ON loan_base_info.area_id = base_area.area_id LEFT JOIN loan_process ON orders.id = loan_process.order_id LEFT JOIN material_audit ON orders.material_audit_id = material_audit.id
case when #{maxGroupLevel} = 0 then false when #{maxGroupLevel} = 1 then loan_base_info.salesman_id in (
#{item}
select temp.id from (SELECT 1 as id) temp where temp.id =2
) else true end and loan_process.material_review != 0
and base.partner_id in
#{item}
and base.bank in
#{item}
) t where remitdate between #{startDate} and #{endDate}
AND TO_DAYS(t.express_receive_date) <= TO_DAYS(#{startDate1})
AND TO_DAYS(t.express_receive_date) <= TO_DAYS(#{endDate1})
AND TO_DAYS(t.materialReviewSubmit) <= TO_DAYS(#{startDate2})
AND TO_DAYS(t.materialReviewSubmit) <= TO_DAYS(#{endDate2})
order by t.express_receive_date desc

