select customer.`name` AS name, customer.id_card AS idCard, DATE_FORMAT(fund.lend_date , '%Y-%m-%d' ) AS lendDate, DATE_FORMAT(record.lend_date, '%Y-%m-%d' ) AS bankLendDate, DATE_FORMAT(fund.repay_date, '%Y-%m-%d' ) AS repayDate, datediff( DATE_FORMAT( fund.repay_date, '%Y-%m-%d' ), DATE_FORMAT( fund.lend_date, '%Y-%m-%d' ) ) AS differTime, remit.remit_amount AS remitAmount, financial.bank_period_principal AS bankPeriodPrincipal, financial.bank_period_principal - remit.remit_amount AS differMoney, CASE WHEN fund.repay_type = 1 THEN '银行放款' WHEN fund.repay_type = 2 THEN '客户退单' ELSE '银行拒贷' END AS repayType, fund.interest AS interest, fund.poundage AS poundage, fund.repay_remark AS repayRemark from (select * from loan_process_bridge where bridge_repay_info=2) process LEFT JOIN loan_order loan ON process.order_id = loan.id LEFT JOIN loan_base_info base ON loan.loan_base_info_id=base.id LEFT JOIN third_party_fund_business fund ON (loan.id=fund.order_id and fund.bridge_procecss_id=process.id) LEFT JOIN loan_customer customer ON loan.loan_customer_id = customer.id LEFT JOIN loan_financial_plan financial ON financial.id = loan.loan_financial_plan_id LEFT JOIN remit_details remit ON loan.remit_details_id =remit.id left join bank_lend_record record on record.loan_order = loan.id where 1=1
AND fund.repay_date >= #{startRepayDate}
AND fund.repay_date >= #{endRepayDate}

select fund.lend_date AS lendDate, fund.repay_date AS repayDate, remit.remit_amount AS remitAmount, customer.`name` AS name, customer.id_card AS idCard, financial.bank_period_principal AS bankPeriodPrincipal from (select * from loan_process_bridge where bridge_interest_record=2) process LEFT JOIN loan_order loan ON process.order_id = loan.id LEFT JOIN loan_base_info base ON loan.loan_base_info_id=base.id LEFT JOIN third_party_fund_business fund ON loan.id=fund.order_id LEFT JOIN loan_customer customer ON loan.loan_customer_id = customer.id LEFT JOIN loan_financial_plan financial ON financial.id = loan.loan_financial_plan_id LEFT JOIN remit_details remit ON loan.remit_details_id =remit.id where 1=1
fund.repay_date >= #{startRepayDate}
fund.repay_date >= #{endRepayDate}
customer.name =#{name}
AND fund.lend_date >= #{startLendDate}
AND fund.lend_date <= #{endLendDate}

SELECT pcustomer.id AS pCustomerId, pcustomer.`name` AS pName, pcustomer.name_pinyin AS pyName, CASE WHEN pcustomer.sex = 1 THEN '男' WHEN pcustomer.sex = 2 THEN '女' ELSE NULL END AS pSex, pcustomer.birth AS pBirth, pcustomer.id_card AS pIdCard, pcustomer.identity_validity AS pIdentityValidity,
CASE WHEN pcustomer.marry = 0 THEN '未婚' WHEN pcustomer.marry = 1 THEN '已婚' ELSE NULL END AS pMarry,
