FROM loan_order principal_orders LEFT JOIN loan_customer pcustomer ON principal_orders.loan_customer_id = pcustomer.id LEFT JOIN loan_financial_plan ON principal_orders.loan_financial_plan_id = loan_financial_plan.id LEFT JOIN loan_car_info ON principal_orders.loan_car_info_id = loan_car_info.id LEFT JOIN car_detail ON loan_car_info.car_detail_id = car_detail.id LEFT JOIN car_model ON car_detail.model_id = car_model.id LEFT JOIN car_brand ON car_model.brand_id = car_brand.id LEFT JOIN vehicle_information ON principal_orders.vehicle_information_id = vehicle_information.id LEFT JOIN loan_base_info ON principal_orders.loan_base_info_id = loan_base_info.id left join base_area area on vehicle_information.apply_license_plate_area = area.area_id LEFT JOIN base_area parea ON area.parent_area_id = parea.area_id
