SELECT result.order_id AS orderId, result.id AS loanCustomerId, result.name AS customerName, result.employee_id AS employeeId, result.bank_credit_employee_id AS bankCreditEmployeeId, result.id_card AS idCard FROM ( SELECT process.order_id, customer.id, customer.`name`, customer.credit_exp_flag, customer.id_card, process.credit_apply, process.bank_credit_record, ( SELECT create_time FROM loan_process_log log WHERE log.task_definition_key = 'usertask_credit_apply' AND order_id = process.order_id ORDER BY create_time DESC LIMIT 1 ) AS credit_apply_time, ( SELECT create_time FROM loan_process_log log WHERE log.task_definition_key = 'usertask_bank_credit_record' AND order_id = process.order_id ORDER BY create_time DESC LIMIT 1 ) AS bank_credit_time, ( SELECT user_id FROM loan_process_log log WHERE log.task_definition_key = 'usertask_credit_apply' AND order_id = process.order_id ORDER BY create_time DESC LIMIT 1 ) AS employee_id, ( SELECT user_id FROM loan_process_log log WHERE log.task_definition_key = 'usertask_bank_credit_record' AND order_id = process.order_id ORDER BY create_time DESC LIMIT 1 ) AS bank_credit_employee_id FROM loan_process process LEFT JOIN loan_order loan ON process.order_id = loan.id LEFT JOIN loan_base_info base ON base.id=loan.loan_base_info_id LEFT JOIN loan_customer customer ON ( loan.loan_customer_id = customer.principal_cust_id AND customer.cust_type != 4 ) WHERE base.bank = #{bankName} ) result WHERE 1=1
AND result.credit_apply_time >= #{creditApplyStartTime}
AND result.credit_apply_time <= #{creditApplyEndTime}
AND result.bank_credit_time >= #{creditQueryStartTime}
AND result.bank_credit_time <= #{creditQueryEndTime}
AND CASE WHEN #{creditStatus} = 3 THEN result.bank_credit_employee_id =#{employeeId} ELSE result.employee_id =#{employeeId} END
AND CASE WHEN #{creditStatus} = 1 THEN result.credit_exp_flag =1 WHEN #{creditStatus} = 2 THEN result.credit_exp_flag =2 WHEN #{creditStatus} = 3 THEN result.bank_credit_time IS NOT NULL END
