principal_base ON principal_orders.loan_base_info_id = principal_base.id LEFT JOIN partner principal_partner ON principal_partner.id = principal_base.partner_id LEFT JOIN base_area principal_area ON principal_area.area_id = principal_base.area_id LEFT JOIN employee employee ON employee.id = principal_base.salesman_id
and credit_info.type = 2 and credit_info.gmt_modify between #{startDate} and #{endDate}
and principal_base.partner_id in
#{item}
and principal_base.bank in
