SELECT base_area.area_name, customer. NAME AS customer_name, customer.id_card AS customer_id_card, customer.mobile AS customer_mobile, loaninfo.bank, partner. NAME AS partner_name, employee. NAME AS salesman_name, case when car.car_type = 0 then '新车' when car.car_type = 1 then '二手车' else '其他' end as car_type, cardetail.price, loan_financial_plan.sign_rate, loan_financial_plan.loan_amount, loan_financial_plan.down_payment_money, loan_financial_plan.bank_period_principal, remit_details.remit_amount, orders.gmt_create, ( SELECT loan_process_log.create_time FROM loan_process_log WHERE order_id = orders.id AND task_definition_key = 'usertask_remit_review' ORDER BY create_time DESC LIMIT 1 ) as submitTime, ( SELECT loan_process_log.user_name FROM loan_process_log WHERE order_id = orders.id AND task_definition_key = 'usertask_remit_review' ORDER BY create_time DESC LIMIT 1 ) as username FROM loan_order orders LEFT JOIN loan_customer customer ON orders.loan_customer_id = customer.id LEFT JOIN loan_base_info loaninfo ON orders.loan_base_info_id = loaninfo.id LEFT JOIN base_area ON loaninfo.area_id = base_area.area_id LEFT JOIN loan_car_info car ON orders.loan_car_info_id = car.id LEFT JOIN car_detail cardetail ON cardetail.id = car.car_detail_id LEFT JOIN loan_financial_plan ON orders.loan_financial_plan_id = loan_financial_plan.id LEFT JOIN remit_details ON orders.remit_details_id = remit_details.id LEFT JOIN loan_process process ON orders.id = process.order_id LEFT JOIN loan_base_info base ON orders.loan_base_info_id = base.id LEFT JOIN employee employee ON base.salesman_id = employee.id LEFT JOIN partner partner ON partner.id = base.partner_id where 1=1 and process.loan_review = 1 and case when #{maxGroupLevel} = 0 then false when #{maxGroupLevel} = 1 then loaninfo.salesman_id in (
#{item}
select temp.id from (SELECT 1 as id) temp where temp.id =2
) else true end
and base.partner_id in
#{item}
and base.bank in
#{item}
having submitTime between #{startDate} and #{endDate} order by submitTime desc

SELECT t.*, CASE WHEN t.express_receive_date IS NOT NULL THEN floor( ( UNIX_TIMESTAMP( DATE_FORMAT( t.express_receive_date, '%Y-%m-%d' ) ) - UNIX_TIMESTAMP( DATE_FORMAT(t.remitdate, '%Y-%m-%d') ) ) / (24 * 60 * 60) ) ELSE floor( ( UNIX_TIMESTAMP( DATE_FORMAT(now(), '%Y-%m-%d') ) - UNIX_TIMESTAMP( DATE_FORMAT(t.remitdate, '%Y-%m-%d') ) ) / (24 * 60 * 60) ) END AS remitOverdueTime, CASE WHEN t.complete_material_date IS NOT NULL THEN floor( ( UNIX_TIMESTAMP( DATE_FORMAT( t.complete_material_date, '%Y-%m-%d' ) ) - UNIX_TIMESTAMP( DATE_FORMAT(t.remitdate, '%Y-%m-%d') ) ) / (24 * 60 * 60) ) ELSE floor( ( UNIX_TIMESTAMP( DATE_FORMAT(now(), '%Y-%m-%d') ) - UNIX_TIMESTAMP( DATE_FORMAT(t.remitdate, '%Y-%m-%d') ) ) / (24 * 60 * 60) ) END AS revievOverdueTime FROM ( SELECT base_area.area_name, partner. NAME AS partner_name, customer. NAME AS customer_name, customer.id_card, loan_base_info.bank, loan_financial_plan.bank_period_principal, (SELECT create_time FROM loan_process_log WHERE orders.id = loan_process_log.order_id AND loan_process_log.task_definition_key = 'usertask_remit_review' ORDER BY id DESC LIMIT 1) AS remitdate, ( SELECT loan_data_flow.express_receive_date FROM loan_data_flow WHERE orders.id = loan_data_flow.order_id AND type = 2 ) AS express_receive_date,
material_audit.complete_material_date, (SELECT create_time FROM loan_process_log WHERE orders.id = loan_process_log.order_id AND loan_process_log.task_definition_key = 'usertask_material_review' ORDER BY id DESC LIMIT 1) AS materialReviewSubmit, case when loan_process.material_review = 1 then '已提交' when loan_process.material_review = 2 then '未提交' when loan_process.material_review = 2 then '已提交' else '未知' end as material_review, ( SELECT count(1) FROM loan_info_supplement WHERE loan_info_supplement.order_id = orders.id ) AS supplementCount, ( SELECT GROUP_CONCAT( DATE_FORMAT(end_time, '%Y-%m-%d '), content ORDER BY id DESC ) FROM loan_info_supplement WHERE loan_info_supplement.order_id = orders.id ) AS supplementContent, ( SELECT GROUP_CONCAT( DATE_FORMAT(start_time, '%Y-%m-%d至'), DATE_FORMAT(end_time, '%Y-%m-%d') ORDER BY id DESC ) FROM loan_info_supplement WHERE loan_info_supplement.order_id = orders.id ) AS supplementTime,
( SELECT create_time FROM loan_process_log WHERE orders.id = loan_process_log.order_id AND task_definition_key = 'usertask_vehicle_information' ) AS carMaterialCreateTime,
( SELECT loan_data_flow.express_send_date FROM loan_data_flow WHERE orders.id = loan_data_flow.order_id AND type = 3 ORDER BY id DESC LIMIT 1 ) AS express_sendbank_date FROM loan_order orders LEFT JOIN loan_customer customer ON orders.loan_customer_id = customer.id LEFT JOIN loan_base_info ON orders.loan_base_info_id = loan_base_info.id LEFT JOIN loan_financial_plan ON orders.loan_financial_plan_id = loan_financial_plan.id LEFT JOIN loan_base_info base ON orders.loan_base_info_id = base.id LEFT JOIN partner partner ON partner.id = base.partner_id LEFT JOIN remit_details ON orders.remit_details_id = remit_details.id LEFT JOIN base_area ON loan_base_info.area_id = base_area.area_id LEFT JOIN loan_process ON orders.id = loan_process.order_id LEFT JOIN material_audit ON orders.material_audit_id = material_audit.id
and loan_process.material_review != 0
and base.partner_id in
#{item}
and base.bank in
#{item}
) t where remitdate between #{startDate} and #{endDate}
AND TO_DAYS(t.express_receive_date) <= TO_DAYS(#{startDate1})
AND TO_DAYS(t.express_receive_date) <= TO_DAYS(#{endDate1})
order by t.express_receive_date desc

SELECT base_area.area_name, partner. NAME AS partner_name, customer. NAME AS customer_name, customer.id_card, customer.mobile AS customer_mobile, loanbaseinfo.bank, case when car.car_type = 0 then '新车' when car.car_type = 1 then '二手车' else '其他' end as car_type, vehicle_information.license_plate_number, cardetail.price, loan_financial_plan.loan_amount, loan_financial_plan.bank_period_principal, ( SELECT create_time FROM loan_process_log WHERE orders.id = loan_process_log.order_id AND loan_process_log.task_definition_key = 'usertask_apply_license_plate_deposit_info' ORDER BY id DESC LIMIT 1 ) AS remitdate, bank_lend_record.lend_date, ( SELECT loan_data_flow.express_send_date FROM loan_data_flow WHERE orders.id = loan_data_flow.order_id AND type = 12 ORDER BY id DESC LIMIT 1 ) AS sendMaterialToParter_date, ( SELECT loan_data_flow.express_receive_date FROM loan_data_flow WHERE orders.id = loan_data_flow.order_id AND type = 12 ORDER BY id DESC LIMIT 1 ) AS ParterReceiveMaterial_date, process.apply_license_plate_deposit_info, CASE WHEN process.apply_license_plate_deposit_info = 1 THEN ( SELECT create_time FROM loan_process_log WHERE order_id = orders.id AND task_definition_key = 'usertask_apply_license_plate_deposit_info' ORDER BY create_time DESC LIMIT 1 ) ELSE NULL END depositTime, CASE WHEN ( SELECT create_time FROM loan_process_log WHERE order_id = orders.id AND task_definition_key = 'usertask_apply_license_plate_deposit_info' ORDER BY create_time DESC LIMIT 1 ) IS NULL THEN floor( ( UNIX_TIMESTAMP( DATE_FORMAT(now(), '%Y-%m-%d') ) - UNIX_TIMESTAMP( DATE_FORMAT( ( SELECT create_time FROM loan_process_log WHERE order_id = orders.id AND task_definition_key = 'usertask_remit_review' ORDER BY create_time DESC LIMIT 1 ), '%Y-%m-%d' ) ) ) / (24 * 60 * 60) ) ELSE floor( ( UNIX_TIMESTAMP( DATE_FORMAT( ( SELECT create_time FROM loan_process_log WHERE order_id = orders.id AND task_definition_key = 'usertask_apply_license_plate_deposit_info' ORDER BY create_time DESC LIMIT 1 ), '%Y-%m-%d' ) ) - UNIX_TIMESTAMP( DATE_FORMAT(lend.lend_date, '%Y-%m-%d') ) ) / (24 * 60 * 60) ) END AS depositOverdueTime, ( SELECT user_name FROM loan_process_log WHERE order_id = orders.id AND task_definition_key = 'usertask_apply_license_plate_deposit_info' ORDER BY create_time DESC LIMIT 1 ) AS submitUser FROM loan_order orders LEFT JOIN loan_customer customer ON orders.loan_customer_id = customer.id LEFT JOIN bank_lend_record lend ON lend.id = orders.bank_lend_record_id LEFT JOIN loan_base_info loanbaseinfo ON orders.loan_base_info_id = loanbaseinfo.id LEFT JOIN base_area ON loanbaseinfo.area_id = base_area.area_id LEFT JOIN loan_car_info car ON orders.loan_car_info_id = car.id LEFT JOIN car_detail cardetail ON cardetail.id = car.car_detail_id LEFT JOIN vehicle_information ON vehicle_information.id = orders.vehicle_information_id LEFT JOIN loan_financial_plan ON orders.loan_financial_plan_id = loan_financial_plan.id LEFT JOIN remit_details ON orders.remit_details_id = remit_details.id LEFT JOIN bank_lend_record ON orders.bank_lend_record_id = bank_lend_record.id LEFT JOIN loan_process process ON orders.id = process.order_id LEFT JOIN loan_base_info base ON orders.loan_base_info_id = base.id LEFT JOIN employee employee ON base.salesman_id = employee.id LEFT JOIN partner partner ON partner.id = base.partner_id
and process.apply_license_plate_deposit_info != 0 and bank_lend_record.lend_date between #{startDate} and #{endDate}
and base.partner_id in
#{item}
and base.bank in
#{item}
order by bank_lend_record.lend_date desc
