mapper.select:
select 

from material_down_his
where order_id = #{orderId,jdbcType=BIGINT}

    and customer_id = #{customerId,jdbcType=BIGINT}


    and task_definition_key = #{taskDefinitionKey,jdbcType=VARCHAR}

mapper.delete:
delete from material_down_his
where task_definition_key = #{taskDefinitionKey,jdbcType=VARCHAR}
  and customer_id = #{customerId,jdbcType=BIGINT}
  and order_id = #{orderId,jdbcType=BIGINT}

mapper.insert:
insert into material_down_his (task_definition_key, customer_id, order_id, 
  url, status, gmt_create
  )
values (#{taskDefinitionKey,jdbcType=VARCHAR}, #{customerId,jdbcType=BIGINT}, #{orderId,jdbcType=BIGINT}, 
  #{url,jdbcType=VARCHAR}, #{status,jdbcType=TINYINT}, #{gmtCreate,jdbcType=TIMESTAMP}
  )

mapper.update:
update material_down_his


    url = #{url,jdbcType=VARCHAR},


    status = #{status,jdbcType=TINYINT},


    gmt_create = #{gmtCreate,jdbcType=TIMESTAMP},


where task_definition_key = #{taskDefinitionKey,jdbcType=VARCHAR}
  and customer_id = #{customerId,jdbcType=BIGINT}
  and order_id = #{orderId,jdbcType=BIGINT}

update material_down_his
set url = #{url,jdbcType=VARCHAR},
  status = #{status,jdbcType=TINYINT},
  gmt_create = #{gmtCreate,jdbcType=TIMESTAMP}
where task_definition_key = #{taskDefinitionKey,jdbcType=VARCHAR}
  and customer_id = #{customerId,jdbcType=BIGINT}
  and order_id = #{orderId,jdbcType=BIGINT}
