mapper.select:
select 

from partner_rela_area
where partner_id = #{partnerId,jdbcType=BIGINT}
  and area_id = #{areaId,jdbcType=BIGINT}

mapper.delete:
delete from partner_rela_area
where partner_id = #{partnerId,jdbcType=BIGINT}
  and area_id = #{areaId,jdbcType=BIGINT}

mapper.update:
update partner_rela_area


    gmt_create = #{gmtCreate,jdbcType=TIMESTAMP},


    gmt_modify = #{gmtModify,jdbcType=TIMESTAMP},


where partner_id = #{partnerId,jdbcType=BIGINT}
  and area_id = #{areaId,jdbcType=BIGINT}

update partner_rela_area
set gmt_create = #{gmtCreate,jdbcType=TIMESTAMP},
  gmt_modify = #{gmtModify,jdbcType=TIMESTAMP}
where partner_id = #{partnerId,jdbcType=BIGINT}
  and area_id = #{areaId,jdbcType=BIGINT}

mapper.select:
select area_id
from partner_rela_area
where partner_id = #{partnerId,jdbcType=BIGINT}
