mapper.select:
select DISTINCT(partner_id)
from partner_rela_employee
where employee_id = #{employeeId,jdbcType=BIGINT}

select employee_id
from partner_rela_employee
where partner_id = #{partnerId,jdbcType=BIGINT}

select

from partner_rela_employee
where partner_id = #{partnerId,jdbcType=BIGINT}
and employee_id = #{employeeId,jdbcType=BIGINT}

mapper.delete:
delete from partner_rela_employee
where partner_id = #{partnerId,jdbcType=BIGINT}
  and employee_id = #{employeeId,jdbcType=BIGINT}

mapper.update:
update partner_rela_employee


        gmt_create = #{gmtCreate,jdbcType=TIMESTAMP},


        gmt_modify = #{gmtModify,jdbcType=TIMESTAMP},


where partner_id = #{partnerId,jdbcType=BIGINT}
and employee_id = #{employeeId,jdbcType=BIGINT}

update partner_rela_employee
set gmt_create = #{gmtCreate,jdbcType=TIMESTAMP},
  gmt_modify = #{gmtModify,jdbcType=TIMESTAMP}
where partner_id = #{partnerId,jdbcType=BIGINT}
  and employee_id = #{employeeId,jdbcType=BIGINT}
