mapper.select:
select 

from partner_white_list
where partner_id = #{partnerId,jdbcType=BIGINT}
  and operation_type = #{operationType,jdbcType=VARCHAR}

mapper.delete:
delete from partner_white_list
where partner_id = #{partnerId,jdbcType=BIGINT}
  and operation_type = #{operationType,jdbcType=VARCHAR}

mapper.insert:
insert into partner_white_list (partner_id, operation_type, white_level, 
  status, gmt_create, gmt_modify
  )
values (#{partnerId,jdbcType=BIGINT}, #{operationType,jdbcType=VARCHAR}, #{whiteLevel,jdbcType=TINYINT}, 
  #{status,jdbcType=TINYINT}, #{gmtCreate,jdbcType=TIMESTAMP}, #{gmtModify,jdbcType=TIMESTAMP}
  )

mapper.update:
update partner_white_list
set white_level = #{whiteLevel,jdbcType=TINYINT},
  status = #{status,jdbcType=TINYINT},
  gmt_create = #{gmtCreate,jdbcType=TIMESTAMP},
  gmt_modify = #{gmtModify,jdbcType=TIMESTAMP}
where partner_id = #{partnerId,jdbcType=BIGINT}
  and operation_type = #{operationType,jdbcType=VARCHAR}
