mapper.select:
select 

from repayment_record
where id_card = #{idCard,jdbcType=VARCHAR}
  and repay_card_id = #{repayCardId,jdbcType=VARCHAR}
  and current_overdue_times = #{currentOverdueTimes,jdbcType=INTEGER}

mapper.delete:
delete from repayment_record
where id_card = #{idCard,jdbcType=VARCHAR}
  and repay_card_id = #{repayCardId,jdbcType=VARCHAR}
  and current_overdue_times = #{currentOverdueTimes,jdbcType=INTEGER}
