mapper.select:
select 

from serial_no
where order_id = #{orderId,jdbcType=BIGINT}
  and serial_no = #{serialNo,jdbcType=BIGINT}

mapper.delete:
delete from serial_no
where order_id = #{orderId,jdbcType=BIGINT}
  and serial_no = #{serialNo,jdbcType=BIGINT}

mapper.insert:
insert into serial_no (order_id, serial_no, operation, 
  ex_message, status, gmt_create
  )
values (#{orderId,jdbcType=BIGINT}, #{serialNo,jdbcType=BIGINT}, #{operation,jdbcType=INTEGER}, 
  #{exMessage,jdbcType=VARCHAR}, #{status,jdbcType=TINYINT}, #{gmtCreate,jdbcType=TIMESTAMP}
  )

mapper.update:
update serial_no
set operation = #{operation,jdbcType=INTEGER},
  ex_message = #{exMessage,jdbcType=VARCHAR},
  status = #{status,jdbcType=TINYINT},
  gmt_create = #{gmtCreate,jdbcType=TIMESTAMP}
where order_id = #{orderId,jdbcType=BIGINT}
  and serial_no = #{serialNo,jdbcType=BIGINT}
