select complex.* from ( select tab.* from ( select main.id AS id, join_e.id AS salesmanId, join_p.id AS partnerId, join_b.area_id AS bizAreaId, join_c.name AS customer, DATE_FORMAT(main.gmt_create,'%Y-%m-%d %H:%i:%S') AS orderGmtCreate, join_c.id_card AS idCard, join_c.mobile AS mobile, join_e.name AS salesman, (select case when END_TIME_ = null then null else DATE_FORMAT(END_TIME_,'%Y-%m-%d %H:%i:%S') end from act_hi_taskinst where PROC_INST_ID_ = main.process_inst_id and TASK_DEF_KEY_ = 'usertask_credit_apply' order by START_TIME_ desc limit 1) AS creditGmtCreate, join_b.bank AS bank, join_d.name AS departmentName, join_f.loan_amount AS loanAmount, join_f.bank_period_principal AS bankPeriodPrincipal, join_f.sign_rate AS signRate, (select case when END_TIME_ = null then null else DATE_FORMAT(END_TIME_,'%Y-%m-%d %H:%i:%S') end from act_hi_taskinst where PROC_INST_ID_ = main.process_inst_id and TASK_DEF_KEY_ = 'usertask_loan_apply' order by START_TIME_ desc limit 1) AS loanGmtCreate, join_i.car_type AS carType, join_h.license_plate_number AS licensePlateNumber, join_p.name AS partner, join_f.loan_time AS loanTime, join_f.down_payment_money AS downPaymentMoney from loan_order main inner join loan_process deputy on main.id = deputy.order_id left join loan_customer join_c on join_c.id = main.loan_customer_id left join loan_base_info join_b on join_b.id = main.loan_base_info_id left join loan_financial_plan join_f on join_f.id = main.loan_financial_plan_id left join loan_car_info join_i on join_i.id = main.loan_car_info_id left join partner join_p on join_p.id = join_b.partner_id left join employee join_e on join_e.id = join_b.salesman_id left join department join_d on join_d.id = join_p.department_id left join vehicle_information join_h on join_h.id = main.vehicle_information_id where 1=1 and deputy.order_status = 1 /** * 多节点查询类型： * 1-征信申请列表【未查询： [提交征信申请单后 , 贷款业务申请单) 】; * 2-征信申请列表【已查询： [贷款业务申请单 , end] 】; * 3-贷款申请列表【待审核： [提交贷款申请单后 , 电审通过) 】; * 4-贷款申请列表【已审核： [电审通过后 , end] 】; * 5-客户查询列表【在贷客户： [提交征信申请单后 , 未放款) 】; * 6-客户查询列表【已贷客户： [已放款 , end] 】; */
AND case when join_b.loan_amount > 1 then deputy.bank_credit_record = 2 OR deputy.social_credit_record = 2 else deputy.bank_credit_record = 2 end
AND case when join_b.loan_amount > 1 then deputy.bank_credit_record = 1 AND deputy.social_credit_record = 1 else deputy.bank_credit_record = 1 end
AND (deputy.loan_apply = 1) AND ((deputy.telephone_verify != 0 and deputy.telephone_verify != 1) OR deputy.visit_verify = 2 OR deputy.visit_verify = 3 )
AND deputy.telephone_verify = 1
AND deputy.credit_apply = 1 AND deputy.remit_review != 1
AND deputy.remit_review = 1
AND 1=2
) tab where 1=1 AND CASE WHEN #{maxGroupLevel} = 0 THEN false when #{maxGroupLevel} = 1 then tab.salesmanId in
#{item}
else true end
AND tab.customer = #{customer}
) complex where 1=1 and case when #{maxGroupLevel} = 3 then 1=1 else 1=1
and complex.bizAreaId in
#{item}
and complex.bank in
#{item}
end order by complex.orderGmtCreate desc

select complex.* from ( select task.*, taskdis.status as taskDisStatus, taskdis.sendee as receiveManId, taskdis.sendee_name as receiveManName from ( SELECT jorder.id AS taskId, result.PROC_INST_ID_ AS processId, result.TASK_DEF_KEY_ AS taskKey, DATE_FORMAT(result.CREATE_TIME_,'%Y-%m-%d %H:%i:%S') AS createScheduleDate, null AS taskDescription, jorder.id AS orderId, jp.name AS partner, je.name AS salesman, jb.bank AS bank, jc.name AS name, jc.id_card AS idCard, jc.mobile AS mobile, ji.car_type AS carType, useGetCarName(ji.car_detail_id) AS carName, jf.down_payment_ratio AS downPaymentRatio, jf.loan_time AS loanTime, jf.loan_amount AS loanAmount, CONCAT('待办',result.NAME_) AS taskStatusExplanation, jb.partner_id AS partnerId, jb.salesman_id AS salesmanId, jb.area_id AS bizAreaId, case when result.TASK_DEF_KEY_ = 'usertask_telephone_verify' then case when lp.telephone_verify < #{telephoneVerifyLevel} and lp.telephone_verify!=1 and lp.telephone_verify!=0 then 2 else 1 end else 2 end AS filtration, null AS supplementOrderId, null AS supplementType, null AS supplementTypeText, null AS his_id, null AS refund_id, null AS sendee,
case when lp.order_status = 1 and lp.loan_apply = 3 and (select reject_origin_task from loan_reject_log where order_id = jorder.id and reject_to_task = 'usertask_loan_apply' order by gmt_create DESC limit 1) = 'usertask_material_review' then false else true end AS canUpdateLoanApply from ( SELECT DISTINCT RES.* FROM ACT_RU_TASK RES inner join ACT_RU_IDENTITYLINK I ON I.TASK_ID_ = RES.ID_ WHERE RES.ASSIGNEE_ IS NULL AND I.TYPE_ = 'candidate' AND ( I.GROUP_ID_ IN ( SELECT name FROM user_group g INNER JOIN (SELECT user_group_id FROM employee_rela_user_group WHERE employee_id = #{employeeId}) e ON e.user_group_id = g.id ) ) ) result INNER JOIN loan_order jorder ON jorder.process_inst_id = result.PROC_INST_ID_ LEFT JOIN loan_base_info jb ON jb.id = jorder.loan_base_info_id LEFT JOIN partner jp ON jp.id = jb.partner_id LEFT JOIN employee je ON je.id = jb.salesman_id LEFT JOIN loan_customer jc ON jc.id = jorder.loan_customer_id LEFT JOIN loan_car_info ji ON ji.id = jorder.loan_car_info_id LEFT JOIN loan_financial_plan jf ON jf.id = jorder.loan_financial_plan_id INNER JOIN loan_process lp ON jorder.id = lp.order_id and lp.order_status = 1 having filtration = 2 union all
SELECT supplement.id AS taskId, jorder.process_inst_id AS processId, ("usertask_info_supplement") AS taskKey, DATE_FORMAT(supplement.start_time,'%Y-%m-%d %H:%i:%S') AS createScheduleDate, null AS taskDescription, jorder.id AS orderId, jp.name AS partner, je.name AS salesman, jb.bank AS bank, jc.name AS name, jc.id_card AS idCard, jc.mobile AS mobile, ji.car_type AS carType, useGetCarName(ji.car_detail_id) AS carName, jf.down_payment_ratio AS downPaymentRatio, jf.loan_time AS loanTime, jf.loan_amount AS loanAmount, ("待办资料增补") AS taskStatusExplanation, jb.partner_id AS partnerId, jb.salesman_id AS salesmanId, jb.area_id AS bizAreaId, (2) AS filtration, supplement.id AS supplementOrderId, supplement.type AS supplementType, useSupplementTypeTxt(supplement.type) AS supplementTypeText, null AS his_id, null AS refund_id, null AS sendee, case when lp.order_status = 1 and lp.loan_apply = 3 and (select reject_origin_task from loan_reject_log where order_id = jorder.id and reject_to_task = 'usertask_loan_apply' order by gmt_create DESC limit 1) = 'usertask_material_review' then false else true end AS canUpdateLoanApply from (select * from loan_info_supplement where status = 2 ) supplement INNER JOIN loan_order jorder ON jorder.id = supplement.order_id LEFT JOIN loan_base_info jb ON jb.id = jorder.loan_base_info_id LEFT JOIN partner jp ON jp.id = jb.partner_id LEFT JOIN employee je ON je.id = jb.salesman_id LEFT JOIN loan_customer jc ON jc.id = jorder.loan_customer_id LEFT JOIN loan_car_info ji ON ji.id = jorder.loan_car_info_id LEFT JOIN loan_financial_plan jf ON jf.id = jorder.loan_financial_plan_id INNER JOIN loan_process lp ON jorder.id = lp.order_id and lp.order_status = 1 having filtration = 2 union all
SELECT his.id AS taskId, jorder.process_inst_id AS processId, case when his.status = 3 then 'usertask_financial_scheme_modify_apply' when his.status !=1 and his.status !=3 then 'usertask_financial_scheme_modify_apply_review' else null end AS taskKey, DATE_FORMAT( case when his.status = 3 then (select gmt_create from loan_reject_log where reject_to_task = 'usertask_financial_scheme_modify_apply' order by gmt_create desc limit 1 ) when his.status !=1 and his.status !=3 then (select create_time from loan_process_log where task_definition_key = 'usertask_financial_scheme_modify_apply' order by create_time desc limit 1) else null end ,'%Y-%m-%d %H:%i:%S') AS createScheduleDate, NULL AS taskDescription, jorder.id AS orderId, jp.name AS partner, je.name AS salesman, jb.bank AS bank, jc.name AS name, jc.id_card AS idCard, jc.mobile AS mobile, ji.car_type AS carType, useGetCarName(ji.car_detail_id) AS carName, jf.down_payment_ratio AS downPaymentRatio, jf.loan_time AS loanTime, jf.loan_amount AS loanAmount, case when his.status = 3 then '待办金融方案修改申请' when his.status !=1 and his.status !=3 then '待办金融方案修改审核' else null end AS taskStatusExplanation, jb.partner_id AS partnerId, jb.salesman_id AS salesmanId, jb.area_id AS bizAreaId, case when his.status = 3 then 2 when his.status !=3 and his.status < #{telephoneVerifyLevel} and his.status !=1 then 2 else 1 end AS filtration, null AS supplementOrderId, null AS supplementType, null AS supplementTypeText, his.id AS his_id, null AS refund_id, null AS sendee, case when lp.order_status = 1 and lp.loan_apply = 3 and (select reject_origin_task from loan_reject_log where order_id = jorder.id and reject_to_task = 'usertask_loan_apply' order by gmt_create DESC limit 1) = 'usertask_material_review' then false else true end AS canUpdateLoanApply from (select * from loan_financial_plan_temp_his where status = 2 or status = 3 ) his INNER JOIN loan_order jorder ON jorder.id = his.order_id LEFT JOIN loan_base_info jb ON jb.id = jorder.loan_base_info_id LEFT JOIN partner jp ON jp.id = jb.partner_id LEFT JOIN employee je ON je.id = jb.salesman_id LEFT JOIN loan_customer jc ON jc.id = jorder.loan_customer_id LEFT JOIN loan_car_info ji ON ji.id = jorder.loan_car_info_id LEFT JOIN loan_financial_plan jf ON jf.id = jorder.loan_financial_plan_id INNER JOIN loan_process lp ON jorder.id = lp.order_id and lp.order_status = 1 having filtration = 2 and case when taskKey = 'usertask_financial_scheme_modify_apply_review' then case when #{telephoneVerifyLevel}>1 then true else false end else true end
union all
SELECT refund.id AS taskId, jorder.process_inst_id AS processId, case when refund.status = 3 then 'usertask_refund_apply' when refund.status = 2 then 'usertask_refund_apply_review' else null end AS taskKey, DATE_FORMAT( case when refund.status = 3 then (select gmt_create from loan_reject_log where reject_to_task = 'usertask_refund_apply' order by gmt_create desc limit 1 ) when refund.status = 2 then (select create_time from loan_process_log where task_definition_key = 'usertask_refund_apply' order by create_time desc limit 1) else null end ,'%Y-%m-%d %H:%i:%S') AS createScheduleDate, NULL AS taskDescription, jorder.id AS orderId, jp.name AS partner, je.name AS salesman, jb.bank AS bank, jc.name AS name, jc.id_card AS idCard, jc.mobile AS mobile, ji.car_type AS carType, useGetCarName(ji.car_detail_id) AS carName, jf.down_payment_ratio AS downPaymentRatio, jf.loan_time AS loanTime, jf.loan_amount AS loanAmount, case when refund.status = 3 then '待办退款申请' when refund.status = 2 then '待办退款申请审核' else null end AS taskStatusExplanation, jb.partner_id AS partnerId, jb.salesman_id AS salesmanId, jb.area_id AS bizAreaId, (2) AS filtration, null AS supplementOrderId, null AS supplementType, null AS supplementTypeText, null AS his_id, refund.id AS refund_id, null AS sendee, case when lp.order_status = 1 and lp.loan_apply = 3 and (select reject_origin_task from loan_reject_log where order_id = jorder.id and reject_to_task = 'usertask_loan_apply' order by gmt_create DESC limit 1) = 'usertask_material_review' then false else true end AS canUpdateLoanApply from (select * from loan_refund_apply where status = 2 or status = 3 ) refund INNER JOIN loan_order jorder ON jorder.id = refund.order_id LEFT JOIN loan_base_info jb ON jb.id = jorder.loan_base_info_id LEFT JOIN partner jp ON jp.id = jb.partner_id LEFT JOIN employee je ON je.id = jb.salesman_id LEFT JOIN loan_customer jc ON jc.id = jorder.loan_customer_id LEFT JOIN loan_car_info ji ON ji.id = jorder.loan_car_info_id LEFT JOIN loan_financial_plan jf ON jf.id = jorder.loan_financial_plan_id INNER JOIN loan_process lp ON jorder.id = lp.order_id and lp.order_status = 1 having filtration = 2 and case when taskKey = 'usertask_refund_apply_review' then case when #{financeLevel} >1 then true else false end else true end union all
