else true end and case when #{maxGroupLevel} = 3 then 1=1 when receiveManId is not null then case when receiveManId = #{employeeId} then 1=1 else 1=2 end else 1=1 end
and task.taskKey = #{key,jdbcType=VARCHAR}
) complex where 1=1 and case when #{maxGroupLevel} = 3 then 1=1 else 1=1
and complex.bizAreaId in
