mapper.select:
select tab.* from ( select main.id AS id, join_e.id AS salesmanId, join_p.id AS partnerId, join_b.area_id AS bizAreaId, join_c.name AS customer, DATE_FORMAT(main.gmt_create,'%Y-%m-%d') AS orderGmtCreate, join_c.id_card AS idCard, join_c.mobile AS mobile, join_e.name AS salesman, (select case when END_TIME_ = null then null else DATE_FORMAT(END_TIME_,'%Y-%m-%d %H:%i:%S') end from act_hi_taskinst where PROC_INST_ID_ = main.process_inst_id and TASK_DEF_KEY_ = 'usertask_credit_apply' order by START_TIME_ desc limit 1) AS creditGmtCreate, join_b.bank AS bank, join_d.name AS departmentName, join_f.loan_amount AS loanAmount, join_f.bank_period_principal AS bankPeriodPrincipal, join_f.sign_rate AS signRate, (select case when END_TIME_ = null then null else DATE_FORMAT(END_TIME_,'%Y-%m-%d %H:%i:%S') end from act_hi_taskinst where PROC_INST_ID_ = main.process_inst_id and TASK_DEF_KEY_ = 'usertask_loan_apply' order by START_TIME_ desc limit 1) AS loanGmtCreate, join_i.car_type AS carType, join_l.license_plate_number AS licensePlateNumber, join_p.name AS partner, join_f.loan_time AS loanTime, join_f.down_payment_money AS downPaymentMoney, join_s.type AS supplementType, join_s.content AS supplementContent, join_s.start_time AS supplementStartTime, join_s.id AS supplementOrderId, 0 AS overdueNum, join_s.status AS taskStatus
from loan_order main inner join loan_process deputy on main.id = deputy.order_id left join loan_customer join_c on join_c.id = main.loan_customer_id left join loan_base_info join_b on join_b.id = main.loan_base_info_id left join loan_financial_plan join_f on join_f.id = main.loan_financial_plan_id left join loan_car_info join_i on join_i.id = main.loan_car_info_id left join partner join_p on join_p.id = join_b.partner_id left join employee join_e on join_e.id = join_b.salesman_id left join department join_d on join_d.id = join_p.department_id left join apply_license_plate_record join_l on join_l.id = main.apply_license_plate_record_id left join loan_info_supplement join_s on main.id = join_s.order_id where 1=1 and case when #{taskStatus} = 0 then join_s.status != 0 else join_s.status = #{taskStatus} end ) tab where 1=1
AND tab.id = #{orderId}
AND tab.customer = #{customer}
AND tab.mobile = #{mobile}
AND tab.idCard = #{idCard}
AND tab.salesmanId = #{salesmanId}
AND case when (select level from base_area where area_id = #{bizAreaId}) = 0 then 1=1 when (select level from base_area where area_id = #{bizAreaId}) = 1 then tab.bizAreaId in (select area_id from base_area where parent_area_id = #{bizAreaId}) when (select level from base_area where area_id = #{bizAreaId}) = 2 then tab.bizAreaId = #{bizAreaId} else 1=2 end
AND tab.bank = #{loanBank}
AND tab.supplementType = #{supplementType}
AND tab.loanAmount <= 100000
AND tab.loanAmount > 100000 AND tab.loanAmount <= 300000
AND tab.loanAmount > 300000
AND tab.signRate <= 10
AND tab.signRate > 10 AND tab.signRate <= 20
AND tab.signRate > 20
AND tab.carType = #{carType}
AND tab.partnerId = #{partnerId}
AND tab.loanTime = #{loanTime}
AND TO_DAYS(tab.orderGmtCreate) >= TO_DAYS(#{startOrderGmtCreate})
AND TO_DAYS(tab.orderGmtCreate) <= TO_DAYS(#{endOrderGmtCreate})
AND TO_DAYS(tab.loanGmtCreate) >= TO_DAYS(#{startLoanGmtCreate})
AND TO_DAYS(tab.loanGmtCreate) <= TO_DAYS(#{endLoanGmtCreate})
AND TO_DAYS(tab.creditGmtCreate) >= TO_DAYS(#{startCreditGmtCreate})
AND TO_DAYS(tab.creditGmtCreate) <= TO_DAYS(#{endCreditGmtCreate})
order by tab.orderGmtCreate asc,tab.taskStatus asc

select tab.* from ( select main.id AS id, join_e.id AS salesmanId, join_p.id AS partnerId, join_b.area_id AS bizAreaId, join_c.name AS customer, DATE_FORMAT(main.gmt_create,'%Y-%m-%d') AS orderGmtCreate, join_c.id_card AS idCard, join_c.mobile AS mobile, join_e.name AS salesman, (select case when END_TIME_ = null then null else DATE_FORMAT(END_TIME_,'%Y-%m-%d %H:%i:%S') end from act_hi_taskinst where PROC_INST_ID_ = main.process_inst_id and TASK_DEF_KEY_ = 'usertask_credit_apply' order by START_TIME_ desc limit 1) AS creditGmtCreate, join_b.bank AS bank, join_d.name AS departmentName, join_f.loan_amount AS loanAmount, join_f.bank_period_principal AS bankPeriodPrincipal, join_f.sign_rate AS signRate, (select case when END_TIME_ = null then null else DATE_FORMAT(END_TIME_,'%Y-%m-%d %H:%i:%S') end from act_hi_taskinst where PROC_INST_ID_ = main.process_inst_id and TASK_DEF_KEY_ = 'usertask_loan_apply' order by START_TIME_ desc limit 1) AS loanGmtCreate, join_i.car_type AS carType, join_l.license_plate_number AS licensePlateNumber, join_p.name AS partner, join_f.loan_time AS loanTime, join_f.down_payment_money AS downPaymentMoney, 0 AS overdueNum, case when #{level} = 7 then case when deputy.telephone_verify < 7 and deputy.telephone_verify!= 1 then 2 else 1 end when #{level} = 6 then case when deputy.telephone_verify < 6 and deputy.telephone_verify!= 1 then 2 else 1 end when #{level} = 5 then case when deputy.telephone_verify < 5 and deputy.telephone_verify!= 1 then 2 else 1 end when #{level} = 4 then case when deputy.telephone_verify < 4 and deputy.telephone_verify!= 1 then 2 else 1 end else -1 end AS taskStatus
from loan_order main inner join loan_process deputy on main.id = deputy.order_id left join loan_customer join_c on join_c.id = main.loan_customer_id left join loan_base_info join_b on join_b.id = main.loan_base_info_id left join loan_financial_plan join_f on join_f.id = main.loan_financial_plan_id left join loan_car_info join_i on join_i.id = main.loan_car_info_id left join partner join_p on join_p.id = join_b.partner_id left join employee join_e on join_e.id = join_b.salesman_id left join department join_d on join_d.id = join_p.department_id left join apply_license_plate_record join_l on join_l.id = main.apply_license_plate_record_id ) tab where 1=1 and tab.taskStatus != -1 and case when #{taskStatus} = 0 then tab.taskStatus != 0 else tab.taskStatus = #{taskStatus} end
AND tab.id = #{orderId}
AND tab.customer = #{customer}
AND tab.mobile = #{mobile}
AND tab.idCard = #{idCard}
AND tab.salesmanId = #{salesmanId}
AND case when (select level from base_area where area_id = #{bizAreaId}) = 0 then 1=1 when (select level from base_area where area_id = #{bizAreaId}) = 1 then tab.bizAreaId in (select area_id from base_area where parent_area_id = #{bizAreaId}) when (select level from base_area where area_id = #{bizAreaId}) = 2 then tab.bizAreaId = #{bizAreaId} else 1=2 end
AND tab.bank = #{loanBank}
AND tab.loanAmount <= 100000
AND tab.loanAmount > 100000 AND tab.loanAmount <= 300000
AND tab.loanAmount > 300000
AND tab.signRate <= 10
AND tab.signRate > 10 AND tab.signRate <= 20
AND tab.signRate > 20
AND tab.carType = #{carType}
AND tab.partnerId = #{partnerId}
AND tab.loanTime = #{loanTime}
AND TO_DAYS(tab.orderGmtCreate) >= TO_DAYS(#{startOrderGmtCreate})
AND TO_DAYS(tab.orderGmtCreate) <= TO_DAYS(#{endOrderGmtCreate})
AND TO_DAYS(tab.loanGmtCreate) >= TO_DAYS(#{startLoanGmtCreate})
AND TO_DAYS(tab.loanGmtCreate) <= TO_DAYS(#{endLoanGmtCreate})
AND TO_DAYS(tab.creditGmtCreate) >= TO_DAYS(#{startCreditGmtCreate})
AND TO_DAYS(tab.creditGmtCreate) <= TO_DAYS(#{endCreditGmtCreate})
order by tab.orderGmtCreate asc,tab.taskStatus asc
