select * from ( select main.id AS id, join_e.id AS salesmanId, join_p.id AS partnerId, join_p.area_id AS bizAreaId, join_c.name AS customer, DATE_FORMAT(main.gmt_create,'%Y-%m-%d') AS orderGmtCreate, join_c.id_card AS idCard, join_c.mobile AS mobile, join_e.name AS salesman, (select case when END_TIME_ = null then null else DATE_FORMAT(END_TIME_,'%Y-%m-%d %H:%i:%S') end from act_hi_taskinst where PROC_INST_ID_ = main.process_inst_id and TASK_DEF_KEY_ = 'usertask_credit_apply' order by START_TIME_ desc limit 1) AS creditGmtCreate, join_f.bank AS bank, join_d.name AS departmentName, join_f.loan_amount AS loanAmount, join_f.bank_period_principal AS bankPeriodPrincipal, join_f.sign_rate AS signRate, (select case when END_TIME_ = null then null else DATE_FORMAT(END_TIME_,'%Y-%m-%d %H:%i:%S') end from act_hi_taskinst where PROC_INST_ID_ = main.process_inst_id and TASK_DEF_KEY_ = 'usertask_loan_apply' order by START_TIME_ desc limit 1) AS loanGmtCreate, join_i.car_type AS carType, join_l.license_plate_number AS licensePlateNumber, join_p.name AS partner, join_f.loan_time AS loanTime, join_f.down_payment_money AS downPaymentMoney, join_s.type AS supplementType, join_s.content AS supplementContent, join_s.start_time AS supplementStartTime, 0 AS overdueNum, join_s.status AS taskStatus
from loan_order main inner join loan_process deputy on main.id = deputy.order_id left join loan_customer join_c on join_c.id = main.loan_customer_id left join loan_base_info join_b on join_b.id = main.loan_base_info_id left join loan_financial_plan join_f on join_f.id = main.loan_financial_plan_id left join loan_car_info join_i on join_i.id = main.loan_car_info_id left join partner join_p on join_p.id = join_b.partner_id left join employee join_e on join_e.id = join_b.salesman_id left join department join_d on join_d.id = join_p.department_id left join apply_license_plate_record join_l on join_l.id = main.apply_license_plate_record_id left join loan_info_supplement join_s on main.id = join_s.order_id where 1=1 and case when #{taskStatus} = 0 then join_s.status != 0 else join_s.status = #{taskStatus} end ) tab where 1=1
AND tab.id = #{orderId}
AND tab.customer = #{customer}
AND tab.mobile = #{mobile}
AND tab.idCard = #{idCard}
AND tab.salesmanId = #{salesmanId}
AND tab.bizAreaId = #{bizAreaId}
AND tab.creditGmtCreate >= #{startCreditGmtCreate}
AND tab.creditGmtCreate <= #{endCreditGmtCreate}
AND tab.bank = #{loanBank}
AND tab.supplementType = #{supplementType}
AND tab.loanAmount <= 100000
AND tab.loanAmount > 100000 AND tab.loanAmount <= 300000
AND tab.loanAmount > 300000
AND tab.signRate <= 10
AND tab.signRate > 10 AND tab.signRate <= 20
AND tab.signRate > 20
AND tab.loanGmtCreate = #{loanGmtCreate}
AND tab.carType = #{carType}
AND tab.partnerId = #{partnerId}
AND tab.loanTime = #{loanTime}

