from loan_order main inner join loan_process deputy on main.id = deputy.order_id left join loan_customer join_c on join_c.id = main.loan_customer_id left join loan_base_info join_b on join_b.id = main.loan_base_info_id left join loan_financial_plan join_f on join_f.id = main.loan_financial_plan_id left join loan_car_info join_i on join_i.id = main.loan_car_info_id left join partner join_p on join_p.id = join_b.partner_id left join employee join_e on join_e.id = join_b.salesman_id left join department join_d on join_d.id = join_p.department_id left join apply_license_plate_record join_l on join_l.id = main.apply_license_plate_record_id ) tab where 1=1 and tab.taskStatus != -1 and case when #{taskStatus} = 0 then tab.taskStatus != 0 else tab.taskStatus = #{taskStatus} end
