from loan_order main inner join loan_process deputy on main.id = deputy.order_id left join loan_customer join_c on join_c.id = main.loan_customer_id left join loan_base_info join_b on join_b.id = main.loan_base_info_id left join loan_financial_plan join_f on join_f.id = main.loan_financial_plan_id left join loan_car_info join_i on join_i.id = main.loan_car_info_id left join partner join_p on join_p.id = join_b.partner_id left join employee join_e on join_e.id = join_b.salesman_id left join department join_d on join_d.id = join_p.department_id left join apply_license_plate_record join_l on join_l.id = main.apply_license_plate_record_id where 1=1 and case when #{taskDefinitionKey} = 'usertask_credit_apply' then case when #{taskStatus} = 0 then deputy.credit_apply != 0 else deputy.credit_apply = #{taskStatus} end when #{taskDefinitionKey} = 'usertask_credit_apply_verify' then case when #{taskStatus} = 0 then deputy.credit_apply_verify != 0 else deputy.credit_apply_verify = #{taskStatus} end when #{taskDefinitionKey} = 'usertask_bank_credit_record' then case when #{taskStatus} = 0 then deputy.bank_credit_record != 0 else deputy.bank_credit_record = #{taskStatus} end when #{taskDefinitionKey} = 'usertask_social_credit_record' then case when #{taskStatus} = 0 then deputy.social_credit_record != 0 else deputy.social_credit_record = #{taskStatus} end when #{taskDefinitionKey} = 'usertask_loan_apply' then case when #{taskStatus} = 0 then deputy.loan_apply != 0 else deputy.loan_apply = #{taskStatus} end when #{taskDefinitionKey} = 'usertask_visit_verify' then case when #{taskStatus} = 0 then deputy.visit_verify != 0 else deputy.visit_verify = #{taskStatus} end when #{taskDefinitionKey} = 'usertask_car_insurance' then case when #{taskStatus} = 0 then deputy.car_insurance != 0 else deputy.car_insurance = #{taskStatus} end when #{taskDefinitionKey} = 'usertask_apply_license_plate_record' then case when #{taskStatus} = 0 then deputy.apply_license_plate_record != 0 else deputy.apply_license_plate_record = #{taskStatus} end when #{taskDefinitionKey} = 'usertask_apply_license_plate_deposit_info' then case when #{taskStatus} = 0 then deputy.apply_license_plate_deposit_info != 0 else deputy.apply_license_plate_deposit_info = #{taskStatus} end when #{taskDefinitionKey} = 'usertask_install_gps' then case when #{taskStatus} = 0 then deputy.install_gps != 0 else deputy.install_gps = #{taskStatus} end when #{taskDefinitionKey} = 'usertask_commit_key' then case when #{taskStatus} = 0 then deputy.commit_key != 0 else deputy.commit_key = #{taskStatus} end when #{taskDefinitionKey} = 'usertask_vehicle_information' then case when #{taskStatus} = 0 then deputy.vehicle_information != 0 else deputy.vehicle_information = #{taskStatus} end when #{taskDefinitionKey} = 'usertask_material_review' then case when #{taskStatus} = 0 then deputy.material_review != 0 else deputy.material_review = #{taskStatus} end when #{taskDefinitionKey} = 'usertask_material_print_review' then case when #{taskStatus} = 0 then deputy.material_print_review != 0 else deputy.material_print_review = #{taskStatus} end when #{taskDefinitionKey} = 'usertask_business_review' then case when #{taskStatus} = 0 then deputy.business_review != 0 else deputy.business_review = #{taskStatus} end when #{taskDefinitionKey} = 'usertask_loan_review' then case when #{taskStatus} = 0 then deputy.loan_review != 0 else deputy.loan_review = #{taskStatus} end when #{taskDefinitionKey} = 'usertask_remit_review' then case when #{taskStatus} = 0 then deputy.remit_review != 0 else deputy.remit_review = #{taskStatus} end else 1=2 end ) tab where 1=1
