FROM loan_order main LEFT JOIN loan_process ON main.id = loan_process.order_id LEFT JOIN loan_customer ON main.loan_customer_id = loan_customer.id LEFT JOIN loan_base_info b ON main.loan_base_info_id = b.id LEFT JOIN employee e ON b.salesman_id = e.id LEFT JOIN partner ON b.partner_id = partner.id
AND case when #{taskStatus} = 0 then loan_process.loan_apply in (1,2,3,12) when #{taskStatus} = 2 then loan_process.loan_apply = 2 and loan_process.order_status = 1 when #{taskStatus} = 3 then loan_process.loan_apply = 3 and loan_process.order_status = 1 else 1=1 end
AND CASE WHEN #{maxGroupLevel} = 0 THEN false when #{maxGroupLevel} = 1 then 1 = 1
and b.salesman_id in
#{item}
else true end and case when #{maxGroupLevel} = 3 then 1=1 else 1=1
and partner.id in
#{item}
and b.bank in
#{item}
end
AND loan_customer.`name` LIKE CONCAT('%',#{customer},'%')
AND partner.id = #{partnerId}
AND e.id = #{salesmanId}
AND b.bank = #{loanBank}
AND main.id = #{orderId}
AND loan_customer.id_card = #{idCard}
order by main.gmt_create DESC
