and base.salesman_id in
#{item}
else true end
and case when #{maxGroupLevel} = 3 then 1=1 else 1=1
and par.id in
#{item}
and base.bank in
#{item}
end
AND loan.id = #{orderId}
AND cus.name LIKE CONCAT('%', #{customer} ,'%')
AND cus.id_card = #{idCard}
AND par.id = #{partnerId}
AND base.salesman_id = #{salesmanId}
AND case when (select level from base_area where area_id = #{bizAreaId}) = 0 then 1=1 when (select level from base_area where area_id = #{bizAreaId}) = 1 then base.area_id in (select area_id from base_area where parent_area_id = #{bizAreaId}) when (select level from base_area where area_id = #{bizAreaId}) = 2 then base.area_id = #{bizAreaId} else 1=2 end
