AND TO_DAYS(node.usertask_remit_review_create_time) <= TO_DAYS(#{endRemitGmtCreate})

SELECT loan.id as id, cus.NAME as customer, cus.id_card as idCard, emp.name as salesman, par.`name` as partner, par.partner_code as partnerCode, (#{taskDefinitionKey}) AS taskKey, DATE_FORMAT(loan.gmt_create,'%Y-%m-%d %H:%i:%S') AS orderGmtCreate, jbds.cat as creditDate, loan.id as taskId, task.sendee_name as receiveManName, useGetTaskTypeText(#{taskDefinitionKey},process.bank_credit_record,process.order_status) AS taskTypeText, base.bank as bank FROM loan_order loan LEFT JOIN loan_process process ON process.order_id = loan.id LEFT JOIN `task_distribution` task ON task.`task_id` = loan.id and task.task_key = 'usertask_bank_credit_record' LEFT JOIN `loan_customer` cus ON cus.id = loan.`loan_customer_id` LEFT JOIN loan_base_info base ON base.id = loan.loan_base_info_id LEFT JOIN employee emp ON emp.id = base.salesman_id LEFT JOIN partner par ON par.id = base.partner_id left join current_node_manager node on node.order_id = loan.id left join (SELECT principal_cust_id, MAX(credit_apply_time) cat FROM (SELECT allhis.customer_id,allhis.credit_apply_time,loan_customer.principal_cust_id FROM (select customer_id,max(credit_apply_time) AS credit_apply_time FROM loan_credit_info_bank_his GROUP BY customer_id) allhis LEFT JOIN loan_customer ON allhis.customer_id = loan_customer.id) qnmd GROUP BY principal_cust_id) jbds ON jbds.principal_cust_id= loan.loan_customer_id WHERE process.credit_apply = 1 AND CASE WHEN #{maxGroupLevel} = 0 THEN false when #{maxGroupLevel} = 1 then 1 = 1
and base.salesman_id in
#{item}
else true end
and case when #{maxGroupLevel} = 3 then 1=1 else 1=1
and par.id in
#{item}
and base.bank in
#{item}
end
and task.taskId NOT IN ( ( SELECT distinct task_id FROM task_distribution where task_key = #{taskDefinitionKey} ) )
and case when #{maxGroupLevel} = 3 then 1=1 when task.sendee is not null then case when task.sendee = #{employeeId} then 1=1 else 1=2 end else 1=1 end
AND loan.id = #{orderId}
AND cus.name LIKE CONCAT('%', #{customer} ,'%')
AND base.bank = #{loanBank}
AND cus.id_card = #{idCard}
AND base.salesman_id = #{salesmanId}
AND par.id = #{partnerId}
AND case when (select level from base_area where area_id = #{bizAreaId}) = 0 then 1=1 when (select level from base_area where area_id = #{bizAreaId}) = 1 then base.area_id in (select area_id from base_area where parent_area_id = #{bizAreaId}) when (select level from base_area where area_id = #{bizAreaId}) = 2 then base.area_id = #{bizAreaId} else 1=2 end
AND TO_DAYS(node.usertask_credit_apply_create_time) >= TO_DAYS(#{startCreditGmtCreate})
AND TO_DAYS(node.usertask_credit_apply_create_time) <= TO_DAYS(#{endCreditGmtCreate})
AND loan.id in ( (select id from loan_order where loan_customer_id in (select principal_cust_id from loan_customer where name = #{relevanceCustomerName}) and loan_customer_id not in (select id from loan_customer where name = #{relevanceCustomerName}) ) )
AND process.bank_credit_record != 0 order by jbds.cat DESC ,process.bank_credit_record ASC
AND process.bank_credit_record = 2 AND process.order_status = 1
AND 0 = IF ( ( SELECT count( 0 ) FROM bank_interface_serial WHERE trans_code = 'applyCredit' AND order_id = loan.id ) > 0, 1, 0 ) AND IF ( ( SELECT count( 1 ) FROM loan_customer WHERE principal_cust_id = loan.loan_customer_id AND cust_type IN ( 1, 2, 3 ) AND credit_exp_flag = 1 ) > 0, true,false)
AND 0 = IF ( ( SELECT count( 0 ) FROM bank_interface_serial WHERE trans_code = 'applyCredit' AND order_id = loan.id ) > 0, 1, 0 ) AND IF ( ( SELECT count( 1 ) FROM loan_customer WHERE principal_cust_id = loan.loan_customer_id AND cust_type IN ( 1, 2, 3 ) AND credit_exp_flag = 1 ) > 0, false, true )
AND loan.id in
#{item}
AND loan.id = 1
order by jbds.cat asc

