FROM loan_order main INNER JOIN loan_process deputy ON main.id = deputy.order_id LEFT JOIN loan_customer join_c ON join_c.id = main.loan_customer_id LEFT JOIN loan_base_info join_b ON join_b.id = main.loan_base_info_id LEFT JOIN loan_financial_plan join_f ON join_f.id = main.loan_financial_plan_id LEFT JOIN partner join_p ON join_p.id = join_b.partner_id LEFT JOIN employee join_e ON join_e.id = join_b.salesman_id
WHERE TRUE
AND deputy.order_status = 1
AND useLevelGetTaskStatus(#{telephoneVerifyLevel}, deputy.telephone_verify) != -1 AND CASE WHEN #{taskStatus} = 0 THEN deputy.telephone_verify != 0 ELSE deputy.telephone_verify = #{taskStatus} END ) tab
AND CASE -- maxGroupLevel = 3 --> admin WHEN #{maxGroupLevel} = 3 THEN TRUE ELSE TRUE
AND tab.partnerId IN
#{item}
AND tab.bank IN
#{item}
END
AND tab.id = #{orderId}
AND tab.partnerCode = #{partnerCode}
AND tab.partnerGroup = #{partnerGroup}
AND tab.customer LIKE CONCAT('%', #{customer} ,'%')
AND tab.mobile = #{mobile}
AND tab.idCard = #{idCard}
AND tab.salesmanId = #{salesmanId}
AND TO_DAYS(complex_log.usertask_credit_apply_create_time) >= TO_DAYS(#{startCreditGmtCreate})
AND TO_DAYS(complex_log.usertask_credit_apply_create_time) <= TO_DAYS(#{endCreditGmtCreate})
AND CASE WHEN #{maxGroupLevel} = 0 THEN FALSE WHEN #{maxGroupLevel} = 1 THEN tab.salesmanId IN
#{item}
ELSE TRUE END
) task
