mapper.select:
SELECT err.serial_no AS serialNo, err.order_id AS orderId, DATE_FORMAT(err.create_time,'%Y-%m-%d %H:%i:%S') AS businessDate, customer.NAME AS userName, customer.id_card AS idCard, base.bank AS bankName, car.car_type AS carType, partner.`name` AS partnerName, err.`status` AS `status`, err.ret_message AS reason, err.process_id AS processId, err.task_definition_key AS taskDefinitionKey FROM ( SELECT DISTINCT z.* FROM voucher_err_record z INNER JOIN ( SELECT SUBSTRING_INDEX( GROUP_CONCAT( serial_no ORDER BY create_time DESC SEPARATOR ',' ), ',', 1 ) AS id FROM voucher_err_record GROUP BY order_id, process_id ) g ON g.id = z.serial_no ) err LEFT JOIN loan_order loan ON err.order_id = loan.id LEFT JOIN loan_customer customer ON loan.loan_customer_id = customer.id LEFT JOIN loan_base_info base ON loan.loan_base_info_id = base.id LEFT JOIN loan_car_info car ON loan.loan_car_info_id = car.id LEFT JOIN partner partner ON partner.id = base.partner_id where 1=1
AND err.status=#{status}
AND err.order_id=#{orderId}
AND customer.`name` LIKE CONCAT('%', #{userName} ,'%')
AND customer.id_card =#{idCard}
AND partner.id=#{partnerId}
AND car.car_type=#{carType}
AND base.bank=#{bank}
AND err.create_time >= #{businessDateStart}
AND err.create_time <= #{businessDateEnd}
