mapper.select:
select 

from voucher_record
where order_id = #{orderId,jdbcType=BIGINT}
  and operation_num = #{operationNum,jdbcType=VARCHAR}

mapper.delete:
delete from voucher_record
where order_id = #{orderId,jdbcType=BIGINT}
  and operation_num = #{operationNum,jdbcType=VARCHAR}

mapper.update:
update voucher_record


    voucher_num = #{voucherNum,jdbcType=VARCHAR},


    gmt_create = #{gmtCreate,jdbcType=TIMESTAMP},


where order_id = #{orderId,jdbcType=BIGINT}
  and operation_num = #{operationNum,jdbcType=VARCHAR}

update voucher_record
set voucher_num = #{voucherNum,jdbcType=VARCHAR},
  gmt_create = #{gmtCreate,jdbcType=TIMESTAMP}
where order_id = #{orderId,jdbcType=BIGINT}
  and operation_num = #{operationNum,jdbcType=VARCHAR}
