mapper.select:
select

,

from yunche_board


   and apply_time = #{applyTime,jdbcType=TIMESTAMP}


    and apply_man = #{applyMan,jdbcType=VARCHAR}


    and apply_partment like concat('%',#{applyPartment,jdbcType=VARCHAR},'%')


    and bank like concat('%',#{bank,jdbcType=VARCHAR},'%')


    and level = #{level,jdbcType=CHAR}


    and title like concat('%',#{title,jdbcType=VARCHAR},'%')


    and effective_time between #{startTime,jdbcType=VARCHAR} and #{endTime,jdbcType=VARCHAR}


    and status = #{status,jdbcType=TINYINT}
