SELECT b.id AS id, d.NAME AS cName, d.id_card AS idCArd, f.NAME AS sName, e.NAME AS pName, h.user_name AS hName, DATE_FORMAT(h.create_time,'%Y/%m/%d') AS gDate, i.remit_amount AS remitAmount, g.loan_amount AS loanAmount, g.sign_rate AS signRate, g.bank_period_principal AS bankPeriodPrincipal FROM loan_process a LEFT JOIN loan_order b ON a.order_id = b.id LEFT JOIN loan_base_info c ON b.loan_base_info_id = c.id LEFT JOIN loan_customer d ON b.loan_customer_id = d.id LEFT JOIN partner e ON c.partner_id = e.id LEFT JOIN employee f ON c.salesman_id = f.id LEFT JOIN loan_financial_plan g ON b.loan_financial_plan_id = g.id LEFT JOIN remit_details i ON b.remit_details_id = i.id left join (select DISTINCT z.* from loan_process_log z inner join ( select SUBSTRING_INDEX(GROUP_CONCAT(id order by create_time desc separator ','),',',1) as id from loan_process_log where task_definition_key="usertask_business_review" GROUP BY order_id ) d on d.id = z.id )h on h.order_id=b.id WHERE a.business_review = 1 AND a.order_status = 1
AND h.create_time >= #{gmtCreateStart1}
AND h.create_time <= #{gmtCreateEnd1}
order by h.create_time limit #{startRow}, #{pageSize}

