SELECT c.partner_id AS partnerId, area.`name` AS areaName, par.`name` AS partnerName, COUNT( CASE WHEN ( c.bank = '中国工商银行哈尔滨顾乡支行' ) THEN 1 END ) AS HEB, COUNT( CASE WHEN ( c.bank = '中国工商银行杭州城站支行' ) THEN 1 END ) AS HZ, COUNT( CASE WHEN ( c.bank = '中国工商银行南京江宁支行' ) THEN 1 END ) AS NJ, COUNT( CASE WHEN ( c.bank = '中国工商银行台州路桥支行' ) THEN 1 END ) AS TZ, COUNT( CASE WHEN ( c.bank = '中国工商银行哈尔滨顾乡支行' || c.bank = '中国工商银行杭州城站支行' || c.bank = '中国工商银行南京江宁支行' ||c.bank = '中国工商银行台州路桥支行') THEN 1 END ) AS total FROM loan_process_log a left join loan_order b on b.id = a.order_id left join loan_base_info c on b.loan_base_info_id = c.id left join partner par on par.id = c.partner_id left join biz_area_rela_partner biz on biz.partner_id = par.id left join biz_area area on area.id = biz.biz_area_id where a.task_definition_key = 'usertask_telephone_verify' and a.action =1
AND a.create_time >= #{gmtCreateStart1}
AND a.create_time <= #{gmtCreateEnd1}
