a.user_id in
#{item}
else true end group by c.partner_id

SELECT r.user_id AS userId, r.user_name AS userName, COUNT( CASE WHEN ( r.action = 0 ) THEN 1 END ) AS repulse, TRUNCATE(COUNT( CASE WHEN ( r.action = 0 ) THEN 1 END ) / COUNT( CASE WHEN ( r.action != 4 AND r.action != 5 ) THEN 1 END )*100,2) AS repulseRatio, COUNT( CASE WHEN ( r.action = 1 ) THEN 1 END ) AS pass, TRUNCATE(COUNT( CASE WHEN ( r.action = 1 ) THEN 1 END ) / COUNT( CASE WHEN ( r.action != 4 AND r.action != 5 ) THEN 1 END )*100,2) AS passRatio, COUNT( CASE WHEN ( r.action = 2 ) THEN 1 END ) AS chanel, TRUNCATE(COUNT( CASE WHEN ( r.action = 2 ) THEN 1 END ) / COUNT( CASE WHEN ( r.action != 4 AND r.action != 5 ) THEN 1 END )*100,2) AS chanelRatio, COUNT( CASE WHEN ( r.action = 3 ) THEN 1 END ) AS supplement, TRUNCATE(COUNT( CASE WHEN ( r.action = 3 ) THEN 1 END ) / COUNT( CASE WHEN ( r.action != 4 AND r.action != 5 ) THEN 1 END )*100,2) AS supplementRatio, COUNT( CASE WHEN ( r.action != 4 AND r.action != 5 ) THEN 1 END ) AS total, '100' AS totalRatio FROM ( SELECT DISTINCT z.* FROM loan_process_log z INNER JOIN ( SELECT SUBSTRING_INDEX( GROUP_CONCAT( id ORDER BY create_time DESC SEPARATOR ',' ), ',', 1 ) AS id FROM loan_process_log WHERE task_definition_key = 'usertask_telephone_verify' GROUP BY order_id, action ) c ON z.id = c.id ) r left join loan_order loan on loan.id = r.order_id left join loan_base_info base on base.id = loan.loan_base_info_id left join biz_area_rela_partner biz on biz.partner_id = base.partner_id left join biz_area area on area.id = biz.biz_area_id where 1 =1
AND r.create_time >= #{gmtCreateStart1}
AND r.create_time <= #{gmtCreateEnd1}
AND biz.biz_area_id = #{bizAreaId}
AND base.partner_id = #{partnerId}
and case when #{maxGroupLevel} = 0 then false when #{maxGroupLevel} = 1 then
r.user_id in
#{item}
else true end GROUP BY r.user_id ORDER BY area.id

