SELECT info.NAME AS name, count( info.order_id ) AS bussNum, sum( info.loan_amount ) AS totalAmount, sum( info.loan_amount ) / count( info.order_id ) AS averageAmount FROM ( SELECT DISTINCT z.order_id,e.id,e.NAME,financial.loan_amount FROM loan_process_log z INNER JOIN ( SELECT SUBSTRING_INDEX( GROUP_CONCAT( id ORDER BY create_time DESC SEPARATOR ',' ), ',', 1 ) AS id FROM loan_process_log WHERE task_definition_key = "usertask_remit_review" AND action = 1 GROUP BY order_id ) g ON g.id = z.id LEFT JOIN loan_order loan ON loan.id = z.order_id LEFT JOIN loan_base_info base ON base.id = loan.loan_base_info_id LEFT JOIN employee e ON e.id = base.salesman_id LEFT JOIN loan_financial_plan financial ON financial.id = loan.loan_financial_plan_id where case when(SELECT count(id) from loan_refund_apply where order_id = z.order_id) = 0 then true else false end
AND z.create_time >= #{gmtCreateStart1}
AND z.create_time <= #{gmtCreateEnd1}
and case when #{maxGroupLevel} = 0 then false when #{maxGroupLevel} = 1 then
base.salesman_id in
#{item}
false
else true end
and base.partner_id in
#{item}
) info
GROUP BY info.id

